INSERT INTO `image_main` VALUES ('1', '0', '0', null, null, null, null, 'Img/Image_Main/00-01.jpg', null, null, '庆祝全国人大成立60周年 习近平总书记发表重要讲话', '', '1', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('4', '0', '0', null, null, null, null, 'Img/Image_Main/00-04.jpg', null, null, '中华苏维埃第一次全国代表大会', '1931年11月7日至20日，在江西瑞金召开了中华苏维埃第一次全国代表大会。出席大会的有各革命根据地和白区的代表600多人。大会宣布成立中华苏维埃共和国临时中央政府，选举毛泽东为临时中央政府主席，项英、张国焘为副主席。图为大会会场，主席台上右起第四人是毛泽东、第三人是朱德。', '2', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('5', '0', '0', null, null, null, null, 'Img/Image_Main/00-05.jpg', null, null, '毛泽东开展乡苏维埃建设调查', '毛泽东十分重视乡苏维埃的建设，亲自作了兴国调查和长冈、才溪两乡调查。1933年11月下旬，他到江西上杭县才溪乡进行了10多天的深入调查，召开了区委书记、区苏维埃主席和各部部长调查会，写下了《才溪乡调查》这篇光辉著作。图为才溪乡调查会址。', '3', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('6', '0', '0', null, null, null, null, 'Img/Image_Main/00-06.jpg', null, null, '中华苏维埃第二次全国代表大会', '1934年1月21日至2月1日，中华苏维埃第二次全国代表大会在江西瑞金沙洲坝开幕。毛泽东作了《我们的经济政策》和《关心群众生活，注意工作方法》的报告，总结了土地革命和根据地经济建设的经验。会议选举毛泽东为中央执行委员会主席。图为江西瑞金沙洲坝中华苏维埃共和国临时中央政府大礼堂旧址。', '4', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('7', '0', '0', null, null, null, null, 'Img/Image_Main/00-07.jpg', null, null, '陕甘宁边区参议会', '1939年1月中旬至2月初，在延安召开了陕甘宁边区第一届第一次参议员大会，通过了《陕甘宁边区抗战时期施政纲领》、《陕甘宁边区各级参议会组织条例》。1940年8月晋察冀边区公布《施政纲领》并进行普选。图为陕甘宁边区参议会会场外景。', '5', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('8', '0', '0', null, null, null, null, 'Img/Image_Main/00-08.jpg', null, null, '陕甘宁边区“豆选”', '豆选是陕甘宁边区农民政治参与的一种方式，即用豆子作选票进行选举。图为边区农民正在用黄豆投票。', '6', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('9', '0', '0', null, null, null, null, 'Img/Image_Main/00-09.jpg', null, null, '晋察冀边区参议会', '1943年2月，河北阜平县温塘举行晋察冀边区参议会。图为吕正操（前右二）、聂荣臻（前右三）出席会议并和部分代表合影。', '7', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('10', '0', '0', null, null, null, null, 'Img/Image_Main/00-10.jpg', null, null, '毛泽东发表《新民主主义论》和《论联合政府》', '毛泽东在抗日战争时期发表了《论持久战》、《新民主主义论》和《论联合政府》等重要著作，指出了中国人民建设新中国的正确道路。图为毛泽东在延安窑洞里写作。', '8', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('11', '0', '0', null, null, null, null, 'Img/Image_Main/00-11.jpg', null, null, '制定起临时宪法作用的共同纲领', '1949年9月21日至30日，中国人民政治协商会议第一届全体会议在北平举行。在全国人民代表大会召开之前，它行使等同于全国人民代表大会的职权。会议通过了起临时宪法作用的《中国人民政治协商会议共同纲领》，选举了中华人民共和国中央人民政府委员会，毛泽东当选为中央人民政府主席。图为毛泽东和副主席朱德、刘少奇、宋庆龄、张澜、李济深在主席台上。', '9', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('12', '0', '0', null, null, null, null, 'Img/Image_Main/00-12.jpg', null, null, '通过国徽图案', '1950年6月14日至23日，中国人民政治协商会议第一届全国委员会第二次会议通过了国徽图案，建议中央人民政府委员会采用。图为毛泽东主持中央人民政府委员会第八次会议，讨论国徽图案。', '13', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('13', '0', '0', null, null, null, null, 'Img/Image_Main/00-13.jpg', null, null, '开国大典', '1949年10月1日下午3时，中华人民共和国开国大典在北京天安门广场隆重举行。图为毛泽东主席向全世界庄严宣告，中华人民共和国中央人民政府正式成立。', '10', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('14', '0', '0', null, null, null, null, 'Img/Image_Main/00-14.jpg', null, null, '制定土地改革法', '1950年6月28日，中央人民政府委员会第八次会议讨论通过了《中华人民共和国土地改革法》。图为毛泽东主席在会议上。', '14', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('15', '0', '0', null, null, null, null, 'Img/Image_Main/00-15.jpg', null, null, '制定选举法', '1953年2月，中央人民政府委员会通过了《中华人民共和国全国人民代表大会及地方各级人民代表大会选举法》。图为中央人民政府委员会第二十二次会议，邓小平在会议上作关于选举法的说明报告。', '16', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('16', '0', '0', null, null, null, null, 'Img/Image_Main/00-16.jpg', null, null, '开展普选', '根据选举法规定，从1953年下半年开始，在全国范围内开展普选，选举基层人大代表。参加选举的有278093100人，共选出5669144名基层人民代表大会的代表。图为各地人民踊跃参加选举。', '17', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('18', '0', '0', null, null, null, null, 'Img/Image_Main/00-18.jpg', null, null, '制定新中国第一部法律--婚姻法', '1950年4月13日，中央人民政府委员会第七次会议通过《中华人民共和国婚姻法》。图为北京市优秀公安工作者葛文翠利用黑板报向人民群众宣传婚姻法。', '12', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('19', '0', '0', null, null, null, null, 'Img/Image_Main/00-19.jpg', null, null, '起草宪法', '1954年1月9日，在毛泽东主席主持下，《中华人民共和国宪法草案》的起草工作正式开始。图为毛泽东主席在修改宪法草案。', '18', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('20', '0', '0', null, null, null, null, 'Img/Image_Main/00-20.jpg', null, null, '全民讨论宪法草案', '1954年6月14日，中央人民政府委员会第三十次会议通过《宪法草案和关于公布宪法草案的决议》。决议要求全国地方各级人民政府立即在人民群众中普遍地组织对于宪法草案的讨论，向人民群众广泛地进行对于宪法草案内容的说明，发动人民群众积极提出自己对于宪法草案的修改意见。图为各地群众积极讨论宪法草案。', '19', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('21', '1', '0', null, null, null, null, 'Img/Image_Main/01-01.jpg', null, null, '一届全国人大常委会委员长刘少奇', '', '1', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('22', '1', '0', null, null, null, null, 'Img/Image_Main/01-02.jpg', null, null, '一届全国人大一次会议隆重开幕', '1954年9月15，一届全国人大一次会议在北京中南海怀仁堂隆重开幕。图为会场外景。', '2', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('23', '1', '0', null, null, null, null, 'Img/Image_Main/01-03.jpg', null, null, '毛泽东主持开幕式并致词', '1954年9月15日，毛泽东主席主持一届全国人大一次会议开幕式并致辞。', '3', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('24', '1', '0', null, null, null, null, 'Img/Image_Main/01-04.jpg', null, null, '制定《中华人民共和国宪法》', '1954年9月15日，在北京怀仁堂举行的一届全国人大一次会议上，宪法起草委员会委员刘少奇作关于宪法草案的报告。9月20日，会议以无记名投票方式表决通过《中华人民共和国宪法》。', '4', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('25', '1', '0', null, null, null, null, 'Img/Image_Main/01-05.jpg', null, null, '制定国家建设亟需法律', '1954年9月20日，一届全国人大一次会议通过了全国人民代表大会组织法，21日通过了地方各级人民代表大会和地方各级人民委员会组织法、国务院组织法、人民法院组织法和人民检察院组织法。', '5', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('26', '1', '0', null, null, null, null, 'Img/Image_Main/01-06.jpg', null, null, '选举决定国家机构领导人', '1954年9月27、28日，一届全国人大一次会议选举、决定国家机构领导人。图为宋庆龄、彭德怀、陈叔通、梅兰芳等代表在投票。', '7', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('27', '1', '0', null, null, null, null, 'Img/Image_Main/01-07.jpg', null, null, '制定“一五计划”', '1955年7月30日，出席一届全国人大二次会议的代表举手表决《关于发展国民经济的第一个五年计划的决议》。', '8', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('28', '1', '0', null, null, null, null, 'Img/Image_Main/01-08.jpg', null, null, '全国人大代表在各地视察', '1955年，一届全国人大常委会第十六次会议、第二十次会议分别通过《关于全国人大代表出发视察工作问题的通知》和《关于全国人大代表和省、自治区、直辖市人大代表视察工作的决定》。图为刘少奇、宋庆龄、徐特立、何香凝在地方视察。', '9', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('29', '1', '0', null, null, null, null, 'Img/Image_Main/01-09.jpg', null, null, '重视对“一府两院”的监督', '一届全国人大期间，“一府两院”主要负责人多次向全国人大及其常委会报告工作和做议案说明。图为1956年9月20日，一届全国人大常委会第四十六次会议听取周恩来总理关于苏伊士运河问题的报告。', '14', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('30', '1', '0', null, null, null, null, 'Img/Image_Main/01-10.jpg', null, null, '中国共产党第八次全国代表大会', '1956年9月15日至27日，中国共产党第八次全国代表大会在北京召开。会议通过的《关于政治报告的决议》指出，必须进一步加强人民民主的法制，逐步地系统地制定完备的法律。图为中共八大开幕式场景。', '13', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('31', '1', '0', null, null, null, null, 'Img/Image_Main/01-11.jpg', null, null, '周恩来作政府工作报告', '1954年9月23日，周恩来总理在一届全国人大一次会议上作政府工作报告。', '6', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('32', '1', '0', null, null, null, null, 'Img/Image_Main/01-12.jpg', null, null, '授予中华人民共和国元帅军衔', '1955年9月23日，一届全国人大常委会第二十二次会议通过《关于授予中华人民共和国元帅军衔的决议》。图为毛泽东主席向朱德等10位帅授勋。', '11', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('33', '1', '0', null, null, null, null, 'Img/Image_Main/01-13.jpg', null, null, '根治和开发黄河', '1955年7月30日，一届全国人大二次会议听取并批准了国务院副总理邓子恢关于根治黄河水害和开发黄河水利的综合规划的原则和基本内容的报告。图为出席一届全国人大二次会议的部分代表参观根治和开发黄河的模型展览。', '10', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('34', '1', '0', null, null, null, null, 'Img/Image_Main/01-14.jpg', null, null, '建立广西壮族自治区和宁夏回族自治区', '1957年7月5日，一届全国人大四次会议举行全体会议，听取国务院副总理兼民族事务委员会主任乌兰夫关于建立广西壮族自治区和宁夏回族自治区的报告。', '15', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('35', '1', '0', null, null, null, null, 'Img/Image_Main/01-15.jpg', null, null, '与国外议会交往', '1956年7月4日，刘少奇委员长（右一）、彭真副委员长（中）和芬兰议会访华代表团团长苏克舍拉宁在酒会上亲切交谈。', '12', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('36', '2', '0', null, null, null, null, 'Img/Image_Main/02-01.jpg', null, null, '二届全国人大常委会委员长朱德', '', '1', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('37', '2', '0', null, null, null, null, 'Img/Image_Main/02-02.jpg', null, null, '二届全国人大一次会议开幕', '1959年4月18日，二届全国人大一次会议在北京开幕。图为宋庆龄、刘少奇、毛泽东、朱德、周恩来（自左至右）在二届全国人大一次会议主席台上。', '2', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('38', '2', '0', null, null, null, null, 'Img/Image_Main/02-03.jpg', null, null, '讨论通过国家机构领导人员候选人人选名单', '1959年4月26日，大会主席团成员和各代表组组长、副组长举行了联席会议。根据各代表组讨论结果，讨论和通过了向大会提出的国家机构领导人员候选人人选名单。', '4', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('39', '2', '0', null, null, null, null, 'Img/Image_Main/02-04.jpg', null, null, '通过关于西藏问题的决议', '1959年4月28日，二届全国人大一次会议通过《关于西藏问题的决议》，完全同意国务院对于原西藏地方政府和上层反动集团在1959年3月10日发动叛乱后所采取的各项措施。图为班禅额尔德尼・确吉坚赞在二届全国人大一次会议上发言。', '6', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('40', '2', '0', null, null, null, null, 'Img/Image_Main/02-05.jpg', null, null, '全国人代会首次在人民大会堂举行', '1960年3月30日至4月10日，二届全国人大二次会议在人民大会堂举行。此后，历届历次全国人大会议均在这里举行。', '8', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('41', '2', '0', null, null, null, null, 'Img/Image_Main/02-06.jpg', null, null, '通过特赦决定', '1959年9月17日，二届全国人大常委会第九次会议通过《关于特赦确实改恶从善的罪犯的决定》。图为彭真宣读毛泽东提出的中共中央关于特赦确实已经改恶从善的战争罪犯、反革命罪犯和普通刑事罪犯的建议。', '7', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('42', '2', '0', null, null, null, null, 'Img/Image_Main/02-07.jpg', null, null, '毛泽东主持开幕式', '1959年4月18日，毛泽东主席主持二届全国人大第一次会议开幕式，会议听取周恩来作政府工作报告。', '3', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('43', '2', '0', null, null, null, null, 'Img/Image_Main/02-08.jpg', null, null, '选举决定国家机构领导人', '1959年4月27日，二届全国人大一次会议选举、决定国家机构领导人。图为李济深、刘伯承、沈钧儒、荣毅仁等代表在投票。', '5', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('44', '2', '0', null, null, null, null, 'Img/Image_Main/02-09.jpg', null, null, '周恩来在二届全国人大四次会议上发表讲话', '1963年12月2日，周恩来总理在二届全国人大四次会议上就当前国内外形势和任务发表讲话，并且回答代表们在讨论中提出的问题。', '9', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('45', '3', '0', null, null, null, null, 'Img/Image_Main/03-01.jpg', null, null, '三届全国人大常委会委员长朱德', '', '1', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('46', '3', '0', null, null, null, null, 'Img/Image_Main/03-02.jpg', null, null, '三届全国人大一次会议', '1964年12月21日至1965年1月4日，三届全国人大一次会议在北京举行。图为会场外景。', '2', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('47', '3', '0', null, null, null, null, 'Img/Image_Main/03-03.jpg', null, null, '选举决定国家机构领导人', '1965年1月3日，三届全国人大一次会议选举刘少奇为中华人民共和国主席，宋庆龄、董必武为副主席。根据刘少奇的提名，会议决定周恩来为国务院总理。会议选举产生全国人大常委会，朱德为委员长，彭真等18人为副委员长。图为宋庆龄、周恩来、彭真、毛泽东、刘少奇、朱德、邓小平、董必武、贺龙（自左至右）在大会主席台上。', '4', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('48', '3', '0', null, null, null, null, 'Img/Image_Main/03-04.jpg', null, null, '决定成立西藏自治区', '1965年8月25日，三届全国人大常委会第十五次会议，通过《关于成立西藏自治区的决议》，决定成立西藏自治区。1965年9月1日至9日，西藏自治区一届人大一次会议召开，宣告西藏自治区正式成立。图为会议现场。', '6', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('49', '3', '0', null, null, null, null, 'Img/Image_Main/03-05.jpg', null, null, '完整提出“四个现代化”总目标', '1964年12月22日，周恩来总理在三届全国人大一次会议上作政府工作报告，他提出，“要在不太长的历史时期内，把我国建设成为一个具有现代农业、现代工业，现代国防和现代科学技术的社会主义强国”，第一次提出了完整地指明了“四个现代化”的总目标和实现这一目标的步骤办法。', '3', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('50', '3', '0', null, null, null, null, 'Img/Image_Main/03-06.jpg', null, null, '刘少奇当选国家主席', '1965年1月3日，刘少奇在三届全国人大一次会议上当选中华人民共和国国家主席。', '5', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('51', '4', '0', null, null, null, null, 'Img/Image_Main/04-01.jpg', null, null, '四届全国人大常委会委员长朱德', '', '1', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('52', '4', '0', null, null, null, null, 'Img/Image_Main/04-02.jpg', null, null, '四届全国人大一次会议', '1975年1月13日至17日，四届全国人大一次会议在北京举行。图为大会主席台。', '2', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('53', '4', '0', null, null, null, null, 'Img/Image_Main/04-03.jpg', null, null, '再次提出实现“四个现代化”', '周恩来在四届全国人大一次会议作政府工作报告，再次指出：在本世纪内，全面实现农业、工业、国防和科学技术的现代化，使我国国民经济走在世界的前列，把我国建设成为社会主义的现代化强国。', '3', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('54', '4', '0', null, null, null, null, 'Img/Image_Main/04-04.jpg', null, null, '通过特赦决定', '1975年3月17日，四届全国人大常委会第二次会议通过《关于特赦释放全部在押战争罪犯的决定》，决定对全部在押的293名战争罪犯实行特赦释放，并予以公民权。', '4', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('55', '5', '0', null, null, null, null, 'Img/Image_Main/05-01.jpg', null, null, '五届全国人大常委会委员长叶剑英', '', '1', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('56', '5', '0', null, null, null, null, 'Img/Image_Main/05-02.jpg', null, null, '五届全国人大一次会议', '1978年2月26日至3月5日，五届全国人大一次会议在北京举行。图为庆祝五届全国人大一次会议召开的宣传画。', '2', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('57', '5', '0', null, null, null, null, 'Img/Image_Main/05-03.jpg', null, null, '中国共产党第十一届中央委员会第三次全体会议', '1978年12月18日至22日举行的党的十一届三中全会，决定把党和国家的工作着重点转移到经济建设上来。决定加强社会主义民主和法制建设。全会的公报指出：“为了保障人民民主，必须加强社会主义法制，使民主制度化、法律化，使这种制度和法律具有稳定性、连续性和极大的权威，做到有法可依，有法必依，执法必严，违法必究。从现在起，应当把立法工作摆到全国人民代表大会及其常务委员会的重要议程上来。”', '3', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('58', '5', '0', null, null, null, null, 'Img/Image_Main/05-14.jpg', null, null, '告台湾同胞书', '1978年12月26日，五届全国人大常委会第五次会议通过全国人大常委会《告台湾同胞书》。图为人民日报刊登全国人大常委会告台湾同胞书。', '6', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('59', '5', '0', null, null, null, null, 'Img/Image_Main/05-04.jpg', null, null, '三个月立了七部法', '1979年6月18日至7月1日，五届全国人大二次会议在北京举行。图为全国人大常委会法制委员会主任彭真在会议上作关于七个法律草案的说明。', '7', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('60', '5', '0', null, null, null, null, 'Img/Image_Main/05-05.jpg', null, null, '直接选举扩大到县', '1979年7月1日，五届全国人大二次会议举行全体会议，通过《关于修正宪法若干规定的决议》规定，县、不设区的市、市辖区、人民公社、镇的人民代表大会代表，由选民经过民主协商，无记名投票直接选举。', '8', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('61', '5', '0', null, null, null, null, 'Img/Image_Main/05-06.jpg', null, null, '决定县级以上地方人大设立常委会', '1979年8月14日，西藏自治区三届人大二次会议根据地方组织法关于县级以上人民代表大会设立常委会的规定，选举产生了自治区人大常委会主任、副主任、委员等常委会组成人员。这是我国设立的第一个省级人大常委会。图为西藏自治区人大常委会机关大门。', '9', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('62', '5', '0', null, null, null, null, 'Img/Image_Main/05-07.jpg', null, null, '授权经济特区立法', '1980年8月26日，叶剑英主持召开五届全国人大常委会第十五次会议，通过《广东省经济特区条例》。条例规定在经济特区实行特殊政策和灵活措施，鼓励外商到经济特区投资办企业，赋予经济特区在改革开放方面更大的自主权，并从注册经营、优惠办法、劳动管理、组织管理等方面确立了经济特区的运作模式。', '10', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('63', '5', '0', null, null, null, null, 'Img/Image_Main/05-08.jpg', null, null, '成立特别检察厅和特别法庭', '1980年9月29日，五届全国人大常委会第十六次会议通过关于成立最高人民检察院特别检察厅和最高人民法院特别法庭检察、审判林彪、江青反革命集团主犯的决定。', '12', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('64', '5', '0', null, null, null, null, 'Img/Image_Main/05-09.jpg', null, null, '投票表决通过现行宪法', '1982年12月4日，五届全国人大五次会议以无记名投票方式对宪法修改草案进行表决。到会代表3040，表决结果，赞成票3037张，弃权票3张，反对票0张。图为邓小平、胡耀邦、王震等投票表决宪法，邓颖超、李井泉、彭真、习仲勋等投票表决宪法。', '16', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('65', '5', '0', null, null, null, null, 'Img/Image_Main/05-10.jpg', null, null, '决定将宪法修改草案交付全民讨论', '1982年4月26日，五届全国人大常委会第二十三次会议，通过关于公布《中华人民共和国宪法修改草案》的决议，决定公布宪法修改草案，交付全国各族人民讨论。此后进行的全民讨论规模之大、影响之广，在我国社会主义法制建设史上是空前的。', '13', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('66', '5', '0', null, null, null, null, 'Img/Image_Main/05-11.jpg', null, null, '彭真作宪法修改草案的报告', '1982年11月26日，在五届全国人大五次会议上，宪法修改委员会副主任委员彭真作关于宪法修改草案的报告。', '15', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('67', '5', '0', null, null, null, null, 'Img/Image_Main/05-12.jpg', null, null, '习仲勋宣布宪法修改草案通过', '1982年12月4日，五届全国人大五次会议通过宪法修改草案。全国人大常委会副委员长兼法制委员会主任习仲勋宣布宪法修改草案通过。', '17', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('68', '5', '0', null, null, null, null, 'Img/Image_Main/05-13.jpg', null, null, '邓小平强调：保障人民民主，健全社会主义法制', '1979年1月18日至4月3日，中共中央在北京召开理论务虚会，讨论工作重点转移后的理论工作问题。邓小平作了题为《解放思想，实事求是，团结一致向前看》的著名讲话，深刻阐述了解放思想与社会主义民主的关系，提出了关系国家命运的重大任务。他强调：必须充分保障人民民主，逐步建全社会主义法制，要求继续解放思想，坚持实事求是。图为邓小平在会上讲话。', '4', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('69', '5', '0', null, null, null, null, 'Img/Image_Main/05-15.jpg', null, null, '中国共产党第十二次全国代表大会', '1982年9月1日至6日，中国共产党第十二次全国代表大会在北京举行。胡耀邦在政治报告中强调，社会主义民主的建设必须同社会主义法制的建设紧密结合起来，使社会主义民主制度化、法律化。', '14', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('70', '5', '0', null, null, null, null, 'Img/Image_Main/05-16.jpg', null, null, '邓小平陈云在党的十一届三中全会上。', '1978年12月18日至22日，党的十一届三中全会在北京举行。图为邓小平、陈云在党的十一届三中全会上。', '5', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('71', '6', '0', null, null, null, null, 'Img/Image_Main/06-01.jpg', null, null, '六届全国人大常委会委员长彭真', '', '1', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('72', '6', '0', null, null, null, null, 'Img/Image_Main/06-02.jpg', null, null, '六届全国人大一次会议', '1983年6月6日至21日，第六届全国人民代表大会第一次会议在北京举行。', '3', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('73', '6', '0', null, null, null, null, 'Img/Image_Main/06-03.jpg', null, null, '制定民法通则', '1986年4月12日，六届全国人大四次会议通过民法通则。图为全国人大法律委、全国人大常委会法工委联合召开民法通则草案座谈会，彭真出席会议并讲话。', '11', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('74', '6', '0', null, null, null, null, 'Img/Image_Main/06-04.jpg', null, null, '授权国务院制定有关经济方面的行政法规', '1985年4月10日，六届全国人大三次会议通过关于授权国务院在经济体制改革和对外开放方面可以制定暂行规定或者条例的决定。', '7', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('75', '6', '0', null, null, null, null, 'Img/Image_Main/06-05.jpg', null, null, '制定村委会组织法（试行）', '1987年11月24日，六届全国人大常委会第二十三次会议通过村委会组织法（试行）。图为山东省聊城市莘县大张家镇道西村村委会换届选举大会现场。', '18', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('76', '6', '0', null, null, null, null, 'Img/Image_Main/06-07.jpg', null, null, '制定企业破产法（试行）', '1986年12月2日，六届全国人大常委会第十八次会议通过企业破产法（试行），该法是一部从计划经济向商品经济转变的重要法律。图为1984年7月23日，彭真委员长在沈阳松陵机械公司为起草企业法进行调查研究。', '14', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('77', '6', '0', null, null, null, null, 'Img/Image_Main/06-06.jpg', null, null, '作出“一五”普法决议', '1985年11月22日，六届全国人大常委会举行第十三次会议通过《关于在公民中基本普及法律常识的决议》。决议决定，从1986年起，争取用5年左右时间，有计划、有步骤地在一切有接受教育能力的公民中，普遍进行一次普及法律常识的教育，并且逐步做到制度化、经常化。右图为江苏省丹徒县农村田头法制宣传。', '10', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('78', '6', '0', null, null, null, null, 'Img/Image_Main/06-08.jpg', null, null, '代表视察', '来自香港、澳门、广州等地的全国人大代表在参观北京大钟寺时，看见几十口造型各异的古钟陈放在露天，对此提出批评，并对文物保护提出建议。', '19', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('79', '6', '0', null, null, null, null, 'Img/Image_Main/06-09.jpg', null, null, '加强同地方人大的联系', '1984年7月2日，彭真委员长到天津市人大常委会视察工作。图为彭真委员长就如何发挥地方人大常委会的作用发表讲话。', '5', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('80', '6', '0', null, null, null, null, 'Img/Image_Main/06-10.jpg', null, null, '批准中英香港问题联合声明', '1985年4月10日，六届全国人大三次会议通过《关于批准〈中华人民共和国政府和大不列颠及北爱尔兰联合王国政府关于香港问题的联合声明〉的决定》。图为出席六届全国人大三次会议的广东省代表团举行联组会议，审议国务院提请大会审议批准的中英香港问题联合声明的议案。', '8', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('81', '6', '0', null, null, null, null, 'Img/Image_Main/06-11.jpg', null, null, '彭真邓颖超会见港澳记者', '1985年3月29日，全国人大常委会委员长彭真、全国政协主席邓颖超会见前来采访人代会和政协会议的港澳记者，并回答他们提出的问题。', '6', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('82', '6', '0', null, null, null, null, 'Img/Image_Main/06-12.jpg', null, null, '制定继承法', '1985年4月10日，六届全国人大三次会议通过继承法。图为李先念主席签署关于《中华人民共和国继承法》的主席令。', '9', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('83', '6', '0', null, null, null, null, 'Img/Image_Main/06-13.jpg', null, null, '制定义务教育法', '1986年4月12日，六届全国人大四次会议通过《义务教育法》。图为出席六届全国人大四次会议的长春市特级教师、解放大路小学副校长牟丽芳（左一）就义务教育法草案发言。', '12', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('84', '6', '0', null, null, null, null, 'Img/Image_Main/06-14.jpg', null, null, '制定民族区域自治法', '1984年5月31日，六届全国人大二次会议通过民族区域自治法。图为乌兰夫副主席、阿沛・阿旺晋美副委员长出席全国人大民委召开的民族区域自治法执行情况座谈会并讲话。', '4', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('85', '6', '0', null, null, null, null, 'Img/Image_Main/06-15.jpg', null, null, '批准中葡澳门问题联合声明', '1987年6月23日，六届全国人大常委会第二十一次会议通过《关于批准〈中华人民共和国政府和葡萄牙共和国政府关于澳门问题的联合声明〉的决议》。图为国务委员兼外交部长吴学谦在会上作关于《中华人民共和国政府和葡萄牙共和国政府关于澳门问题的联合声明》草签文本的报告。', '15', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('86', '6', '0', null, null, null, null, 'Img/Image_Main/06-16.jpg', null, null, '制定外资企业法', '1986年4月12日，六届全国人大四次会议通过外资企业法。', '13', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('87', '6', '0', null, null, null, null, 'Img/Image_Main/06-17.jpg', null, null, '制定大气污染防治法', '1987年9月5日，六届全国人大常委会第二十二次会议通过大气污染防治法。', '16', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('88', '6', '0', null, null, null, null, 'Img/Image_Main/06-18.jpg', null, null, '中国共产党第十三次全国代表大会', '1987年10月25日至11月1日，中国共产党第十三次全国代表大会举行。十三大政治报告提出我国处在社会主义初级阶段的理论，提出进行政治体制改革、完善社会主义民主政治的若干制度。', '17', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('89', '7', '0', null, null, null, null, 'Img/Image_Main/07-01.jpg', null, null, '七届全国人大常委会委员长万里', '', '1', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('90', '7', '0', null, null, null, null, 'Img/Image_Main/07-02.jpg', null, null, '七届全国人大一次会议', '1988年3月25日至4月13日，七届人大一次会议在北京人民大会堂举行。', '2', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('91', '7', '0', null, null, null, null, 'Img/Image_Main/07-03.jpg', null, null, '通过宪法修正案', '1988年4月12日，七届全国人大一次会议通过《宪法修正案》，规定:“国家允许私营经济在法律规定的范围内存在和发展。私营经济是社会主义公有制经济的补充。国家保护私营经济的合法的权利和利益，对私营经济实行引导、监督和管理”，“任何组织或者个人不得侵占、买卖或者以其他形式非法转让土地。土地的使用权可以依照法律的规定转让”。', '3', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('92', '7', '0', null, null, null, null, 'Img/Image_Main/07-04.jpg', null, null, '决定设立海南省', '1988年4月13日，七届全国人大一次会议通过《关于设立海南省的决定》、《关于建立海南经济特区的决议》。图为1993年1月海南省一届人大一次会议现场。', '4', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('93', '7', '0', null, null, null, null, 'Img/Image_Main/07-05.jpg', null, null, '制定全民所有制工业企业法', '1988年4月13日，七届全国人大一次会议表决通过全民所有制工业企业法。', '5', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('94', '7', '0', null, null, null, null, 'Img/Image_Main/07-06.jpg', null, null, '制定行政诉讼法', '1989年4月4日，七届全国人大二次会议通过行政诉讼法，打破了几千年的传统观念，建立了民可以告官的行政诉讼制度，是社会主义民主政治建设和社会主义法制建设的一个重要里程碑 。图为2010年1月19日江苏省宿迁市宿城区人民法院行政庭公开审理一起农户状告乡政府非法拆迁的行政诉讼案件。', '6', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('95', '7', '0', null, null, null, null, 'Img/Image_Main/07-07.jpg', null, null, '制定香港特别行政区基本法', '1990年4月4日，七届全国人大三次会议通过《香港特别行政区基本法》及其3个附件和区旗、区徽图案，通过关于设立香港特别行政区的决定、关于香港特别行政区第一届政府和立法会产生办法的决定、关于批准香港特别行政区基本法起草委员会关于设立全国人大常委会香港特别行政区基本法委员会的建议的决定。图为香港特别行政区全景。', '7', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('96', '7', '0', null, null, null, null, 'Img/Image_Main/07-08.jpg', null, null, '制定民事诉讼法', '1991年4月9日，七届全国人大四次会议通过民事诉讼法。图为4月6日大会主席团举行第四次会议，主席团成员就法律委员会关于民事诉讼法（试行）草案等审议结果的报告进行表决。', '8', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('97', '7', '0', null, null, null, null, 'Img/Image_Main/07-09.jpg', null, null, '通过《关于兴建长江三峡工程的决议》', '1992年4月3日，七届全国人大五次会议通过《关于兴建长江三峡工程的决议》。决定批准将兴建长江三峡工程列入国民经济和社会发展十年规划，由国务院根据国民经济发展的实际情况和国家财力、物力的可能，选择适当时机组织实施，对已发现的问题要继续研究、妥善解决。图为三峡工程建设场景。', '10', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('98', '7', '0', null, null, null, null, 'Img/Image_Main/07-10.jpg', null, null, '授权深圳经济特区立法', '1992年7月1日，七届全国人大常委会第26次会议根据七届全国人大二次会议的决定，授权深圳市人大及其常委会根据具体情况和实际需要，遵循宪法的规定以及法律和行政法规的基本原则，制定法规，在深圳经济特区实施，并报全国人大常委会、国务院和广东省人大常委会备案。授权深圳市政府制定规章并在深圳经济特区组织实施。', '14', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('99', '7', '0', null, null, null, null, 'Img/Image_Main/07-11.jpg', null, null, '国务院总理副总理会见中外记者', '1991年4月9日，李鹏总理与田纪云、吴学谦、邹家华副总理会见采访七届全国人大四次会议的中外记者并答记者问。', '9', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('100', '7', '0', null, null, null, null, 'Img/Image_Main/07-12.jpg', null, null, '中国共产党第十四次全国代表大会', '1992年10月12日至18日，中国共产党第十四次全国代表大会召开。中共中央总书记江泽民在政治报告中指出，进一步完善人民代表大会制度，加强立法工作，严格执行宪法和法律。', '15', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('101', '7', '0', null, null, null, null, 'Img/Image_Main/07-13.jpg', null, null, '制定工会法', '1992年4月3日，七届全国人大五次会议通过工会法。图为全国人大常委会副委员长、法律委员会主任委员王汉斌在七届全国人大五次会议上作关于工会法修改草案的说明。', '11', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('102', '7', '0', null, null, null, null, 'Img/Image_Main/07-14.jpg', null, null, '制定妇女权益保护法', '1992年4月3日，七届全国人大五次会议通过妇女权益保护法。图为陈慕华在七届全国人大五次会议上与江西省代表一起审议妇女权益保障法草案。', '12', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('103', '7', '0', null, null, null, null, 'Img/Image_Main/07-15.jpg', null, null, '制定代表法', '1992年4月3日，七届全国人大五次会议通过代表法。图为全国人大常委会副秘书长曹志在七届全国人大五次会议上作关于代表法草案的说明。', '13', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('104', '7', '0', null, null, null, null, 'Img/Image_Main/07-16.jpg', null, null, '纪念现行宪法颁布十周年', '1992年12月4日，首都各界隆重纪念《中华人民共和国宪法》颁布十周年。', '16', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('105', '8', '0', null, null, null, null, 'Img/Image_Main/08-01.jpg', null, null, '八届全国人大常委会委员长乔石', '', '1', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('106', '8', '0', null, null, null, null, 'Img/Image_Main/08-02.jpg', null, null, '八届全国人大一次会议', '1993年3月15日，八届全国人大一次会议在北京开幕。图为国务院总理李鹏作政府工作报告。', '2', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('107', '8', '0', null, null, null, null, 'Img/Image_Main/08-03.jpg', null, null, '通过宪法修正案', '1993年3月29日，八届全国人大一次会议通过宪法修正案，将“我国正处于社会主义初级阶段”、“国家实行社会主义市场经济”写入宪法。', '3', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('108', '8', '0', null, null, null, null, 'Img/Image_Main/08-04.jpg', null, null, '制定公司法', '1993年12月29日，八届全国人大常委会第五次会议通过公司法。', '8', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('109', '8', '0', null, null, null, null, 'Img/Image_Main/08-05.jpg', null, null, '制定消费者权益保护法', '1993年10月31日，八届全国人大常委会第四次会议通过消费者权益保护法。', '7', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('110', '8', '0', null, null, null, null, 'Img/Image_Main/08-06.jpg', null, null, '制定预算法', '1994年3月22日，八届全国人大二次会议通过预算法。', '9', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('111', '8', '0', null, null, null, null, 'Img/Image_Main/08-07.jpg', null, null, '制定劳动法', '1994年7月5日，八届全国人大常委会第八次会议通过劳动法。', '12', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('112', '8', '0', null, null, null, null, 'Img/Image_Main/08-08.jpg', null, null, '修改刑事诉讼法', '1996年3月17日，八届全国人大四次会议，通过《关于修改〈刑事诉讼法〉的决定》。', '15', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('113', '8', '0', null, null, null, null, 'Img/Image_Main/08-14.jpg', null, null, '修改刑法', '1997年3月14日，八届全国人大五次会议通过修订后的《中华人民共和国刑法》。', '17', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('114', '8', '0', null, null, null, null, 'Img/Image_Main/08-09.jpg', null, null, '制定国家赔偿法', '1994年5月12日，八届全国人大常委会第七次会议通过国家赔偿法。', '10', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('115', '8', '0', null, null, null, null, 'Img/Image_Main/08-10.jpg', null, null, '制定澳门特别行政区基本法', '1993年3月31日，八届全国人大一次会议通过澳门特别行政区基本法等六个法律文件。图为澳门特别行政区最具代表性的名胜古迹--大三巴牌坊。', '4', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('116', '8', '0', null, null, null, null, 'Img/Image_Main/08-11.jpg', null, null, '加强对法律实施的检查监督', '1993年9月2日，八届全国人大常委会第三次会议通过《关于加强对法律实施情况检查监督的若干规定》。图为1994年12月27日，在八届全国人大常委会第十一次会议上，倪志福副委员长作关于检查反不正当竞争法、消费者权益保护法、产品质量法和全国人大常委会关于惩治生产、销售伪劣商品犯罪的决定执行情况的报告。', '6', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('117', '8', '0', null, null, null, null, 'Img/Image_Main/08-12.jpg', null, null, '主办各国议会联盟第96届大会', '1996年9月16日至21日，全国人大常委会承办的各国议会联盟第九十六届大会在北京举行。118个国家的议会代表团和23个国际组织的代表参加会议。', '16', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('118', '8', '0', null, null, null, null, 'Img/Image_Main/08-13.jpg', null, null, '中国共产党第十五次全国代表大会', '1997年9月，党的十五大提出“依法治国，建设社会主义法治国家”，并提出了“加强立法工作，提高立法质量，到2010年形成有中国特色社会主义法律体系”的立法工作目标。', '19', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('119', '8', '0', null, null, null, null, 'Img/Image_Main/08-15.jpg', null, null, '制定对外贸易法', '1994年5月12日，八届全国人大常委会第七次会议通过对外贸易法。图为全国人大法律委员会副主任委员项淳一作关于对外贸易法草案的说明。', '11', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('120', '8', '0', null, null, null, null, 'Img/Image_Main/08-16.jpg', null, null, '制定法官法、检察官法和人民警察法', '1995年2月28日，八届全国人大常委会第十二次会议通过法官法、检察官法和人民警察法。', '13', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('121', '8', '0', null, null, null, null, 'Img/Image_Main/08-17.jpg', null, null, '制定教育法', '1995年3月18日，八届全国人大三次会议通过《中华人民共和国教育法》。', '14', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('122', '8', '0', null, null, null, null, 'Img/Image_Main/08-18.jpg', null, null, '批准设立重庆直辖市', '1997年3月14日，八届全国人大五次会议通过《关于批准设立重庆直辖市的决定》，决定批准设立重庆直辖市，撤销四川省原重庆市。图为代表表决关于批准设立重庆直辖市的决定。', '18', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('123', '8', '0', null, null, null, null, 'Img/Image_Main/08-19.jpg', null, null, '制定农业法', '1993年7月2日，八届全国人大常委会第二次会议通过农业法。', '5', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('124', '9', '0', null, null, null, null, 'Img/Image_Main/09-01.jpg', null, null, '九届全国人大常委会委员长李鹏', '', '1', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('125', '9', '0', null, null, null, null, 'Img/Image_Main/09-02.jpg', null, null, '九届全国人大一次会议', '1998年3月5日至19日，九届全国人大一次会议在北京举行。', '2', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('126', '9', '0', null, null, null, null, 'Img/Image_Main/09-03.jpg', null, null, '将依法治国写入宪法', '1999年3月15日，九届全国人大二次会议通过了宪法修正案，将“依法治国、建设社会主义法治国家”写入宪法。', '7', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('127', '9', '0', null, null, null, null, 'Img/Image_Main/09-04.jpg', null, null, '制定立法法', '2000年3月15日，九届全国人大三次会议表决通过立法法。', '11', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('128', '9', '0', null, null, null, null, 'Img/Image_Main/09-05.jpg', null, null, '制定合同法', '1999年3月15日，九届全国人大二次会议通过合同法。经济合同法、涉外经济合同法、技术合同法同时废止。图为李鹏委员长在北京市丰台区法院主持召开座谈会，就合同法草案修改事宜向基层法官征求意见。', '8', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('129', '9', '0', null, null, null, null, 'Img/Image_Main/09-06.jpg', null, null, '制定证券法', '1998年12月29日，九届全国人大常委会第六次会议通过证券法。图为上海证交所交易大厅。', '6', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('130', '9', '0', null, null, null, null, 'Img/Image_Main/09-07.jpg', null, null, '修改完善对外开放的法律环境', '为适应我国加入世界贸易组织和扩大对外开放的需要，九届全国人大及其常委会修改了中外合作经营企业法、外资企业法、海关法、专利法、中外合资经营企业法。图为2001年11月外经贸部部长石广生等在加入世贸组织签字仪式后鼓掌祝贺。', '14', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('131', '9', '0', null, null, null, null, 'Img/Image_Main/09-08.jpg', null, null, '加强经济工作监督', '2000年3月1日，九届全国人大常委会第十四次会议通过关于加强经济工作监督的决定。', '9', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('132', '9', '0', null, null, null, null, 'Img/Image_Main/09-09.jpg', null, null, '李鹏出席千年议长大会', '2000年8月30日，千年议长大会在纽约联合国总部开幕，李鹏委员长作为大会副主席出席会议开幕式并作重要发言。图为李鹏委员长在纽约联合国总部会议大厅主持千年议长大会。', '12', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('133', '9', '0', null, null, null, null, 'Img/Image_Main/09-10.jpg', null, null, '召开通讯员工作座谈会', '1998年12月10日至12日，全国人大常委会办公厅在北京召开《人大工作通讯》和人民日报《民主与法制》专版通讯员工作座谈会。李鹏委员长出席座谈会并讲话。', '5', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('134', '9', '0', null, null, null, null, 'Img/Image_Main/09-11.jpg', null, null, '中国共产党第十六次全国代表大会', '2002年11月8日至14日，中国共产党第十六次全国代表大会在北京举行。江泽民在政治报告中指出，要把坚持党的领导、人民当家作主和依法治国有机统一起来。', '16', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('135', '9', '0', null, null, null, null, 'Img/Image_Main/09-12.jpg', null, null, '纪念现行宪法公布施行20周年', '2002年12月4日，首都各界举行纪念宪法公布施行20周年大会，中共中央总书记胡锦涛出席大会并发表重要讲话。', '17', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('136', '9', '0', null, null, null, null, 'Img/Image_Main/09-13.jpg', null, null, '香港代表团首次独立组团', '1998年3月2日，出席九届全国人大一次会议的香港特别行政区代表团抵达北京。这是香港特别行政区首次独立组团参加全国人大会议。图为香港临时立法会主席范徐丽泰代表在首都机场接受记者采访。', '3', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('137', '9', '0', null, null, null, null, 'Img/Image_Main/09-14.jpg', null, null, '制定村委会组织法', '1998年11月4日，九届全国人大常委会第五次会议通过村委会组织法。左图为河南省商丘市宁陵县孔集乡黄楼村村务公开栏。右图为1998年11月26日，安徽省凤阳县小岗村的259名村民参加第四届村民委员会选举大会，选举新一届村委会干部。这是84岁的选民杨继英在投票。', '4', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('138', '9', '0', null, null, null, null, 'Img/Image_Main/09-15.jpg', null, null, '中央政治局集体学习宪法', '2002年12月26日，中共中央政治局集体学习宪法，中共中央总书记胡锦涛主持学习并发表讲话。', '18', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('139', '9', '0', null, null, null, null, 'Img/Image_Main/09-16.jpg', null, null, '制定计划生育法', '2001年12月29日，九届全国人大常委会第二十五次会议通过人口与计划生育法。', '15', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('140', '9', '0', null, null, null, null, 'Img/Image_Main/09-17.jpg', null, null, '澳门代表团首次独立组团', '2000年3月2日，出席九届全国人大三次会议的澳门特别行政区代表团抵达北京。这是澳门特别行政区首次独立组团参加全国人大会议。图为澳门代表团团长唐星樵代表下飞机后接受记者采访。', '10', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('141', '10', '0', null, null, null, null, 'Img/Image_Main/10-01.jpg', null, null, '十届全国人大常委会委员长吴邦国', '', '1', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('142', '10', '0', null, null, null, null, 'Img/Image_Main/10-02.jpg', null, null, '十届全国人大一次会议', '2003年3月5日至18日，十届全国人大一次会议在北京举行。', '2', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('143', '10', '0', null, null, null, null, 'Img/Image_Main/10-03.jpg', null, null, '将尊重和保障人权写入宪法', '2004年3月14日，十届全国人大二次会议通过宪法修正案，将尊重和保障人权写入宪法。图为王兆国副委员长作宪法修正案说明。', '5', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('144', '10', '0', null, null, null, null, 'Img/Image_Main/10-04.jpg', null, null, '纪念全国人大成立50周年', '2004年9月15日，首都各界纪念全国人民代表大会成立50周年。中共中央总书记、国家主席胡锦涛出席并发表重要讲话。', '6', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('145', '10', '0', null, null, null, null, 'Img/Image_Main/10-05.jpg', null, null, '审议非典报告', '2003年4月25日至26日，十届全国人大常委会举行第二次会议，听取并审议国务院副总理吴仪关于非典型肺炎防治工作的报告。', '3', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('146', '10', '0', null, null, null, null, 'Img/Image_Main/10-06.jpg', null, null, '制定反分裂国家法', '2005年3月14日，十届全国人大三次会议通过反分裂国家法。', '8', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('147', '10', '0', null, null, null, null, 'Img/Image_Main/10-07.jpg', null, null, '制定物权法', '2007年3月16日，十届全国人大五次会议通过物权法。', '17', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('148', '10', '0', null, null, null, null, 'Img/Image_Main/10-08.jpg', null, null, '制定监督法', '2006年8月27日，十届全国人大常委会第二十三次会议通过监督法。', '15', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('149', '10', '0', null, null, null, null, 'Img/Image_Main/10-09.jpg', null, null, '制定劳动合同法', '2007年6月29日，十届全国人大常委会第二十八次会议通过劳动合同法。', '19', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('150', '10', '0', null, null, null, null, 'Img/Image_Main/10-10.jpg', null, null, '废止农业税', '2005年12月29日，十届全国人大常委会第十九次会议通过废止农业税条例的决定。', '14', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('151', '10', '0', null, null, null, null, 'Img/Image_Main/10-11.jpg', null, null, '举行“个税起征点”听证会', '2005年9月27日，全国人大法律委员会、财政经济委员会、全国人大常委会法制工作委员会，共同组织举行个人所得税工薪所得减除费用标准听证会。', '13', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('152', '10', '0', null, null, null, null, 'Img/Image_Main/10-12.jpg', null, null, '贯彻中央九号文件精神', '2005年5月26日，中共中央转发《中共全国人大常委会党组关于进一步发挥全国人大代表作用，加强全国人大常委会制度建设的若干意见》。图为全国人大常委会办公厅组织贯彻中共中央9号文件学习班。', '10', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('153', '10', '0', null, null, null, null, 'Img/Image_Main/10-13.jpg', null, null, '吴邦国出席第二届世界议长大会', '2005年9月7日，吴邦国委员长出席在纽约联合国总部举行的第二届世界议长大会，并在全体会议上作题为《加强多边合作促进共同发展》的重要发言。', '12', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('154', '10', '0', null, null, null, null, 'Img/Image_Main/10-14.jpg', null, null, '中国人大网正式开通', '2004年2月25日，中国人大网试开通。2005年2月25日，中国人大网正式开通。图为盛华仁副委员长兼秘书长在开通仪式上讲话。', '7', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('155', '10', '0', null, null, null, null, 'Img/Image_Main/10-15.jpg', null, null, '中国共产党第十七次全国代表大会', '2007年10月15日至21日，中国共产党第十七次全国代表大会举行。胡锦涛总书记在报告中指出，要全面落实依法治国基本方略，加快建设社会主义法治国家。要坚持科学立法、民主立法，完善中国特色社会主义法律体系。', '21', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('156', '10', '0', null, null, null, null, 'Img/Image_Main/10-16.jpg', null, null, '制定道路交通安全法', '2003年10月28日，十届全国人大常委会第五次会议通过道路交通安全法。', '4', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('157', '10', '0', null, null, null, null, 'Img/Image_Main/10-17.jpg', null, null, '制定反垄断法', '2007年8月30日，十届全国人大常委会第二十九次会议通过反垄断法。', '20', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('158', '10', '0', null, null, null, null, 'Img/Image_Main/10-18.jpg', null, null, '制定治安管理处罚法', '2005年8月28日，十届全国人大常委会第十七次会议通过治安管理处罚法。', '11', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('159', '10', '0', null, null, null, null, 'Img/Image_Main/10-19.jpg', null, null, '制定企业破产法', '2006年8月27日，十届全国人大常委会第二十三次会议通过企业破产法。', '16', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('160', '10', '0', null, null, null, null, 'Img/Image_Main/10-20.jpg', null, null, '制定企业所得税法', '2007年3月16日，十届全国人大第五次会议通过企业所得税法。', '18', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('161', '11', '0', null, null, null, null, 'Img/Image_Main/11-01.jpg', null, null, '十一届全国人大常委会委员长吴邦国', '', '1', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('162', '11', '0', null, null, null, null, 'Img/Image_Main/11-02.jpg', null, null, '十一届全国人大一次会议', '2008年3月5日至18日，十一届全国人大一次会议在北京举行。', '2', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('163', '11', '0', null, null, null, null, 'Img/Image_Main/11-03.jpg', null, null, '审议抗击低温雨雪冰冻灾害情况报告', '2008年4月22日至24日，十一届全国人大常委会举行第二次会议，听取审议国家发改委关于抗击低温雨雪冰冻灾害及灾后重建工作情况的报告。', '3', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('164', '11', '0', null, null, null, null, 'Img/Image_Main/11-04.jpg', null, null, '听取并审议汶川特大地震抗震救灾情况报告', '2008年5月22日，十一届全国人大常委会举行第四次委员长会议，国务院副总理、国务院抗震救灾总指挥部副总指挥李克强代表国务院作关于四川汶川大地震抗震抢险及救灾工作情况的汇报。', '4', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('165', '11', '0', null, null, null, null, 'Img/Image_Main/11-05.jpg', null, null, '法律草案公开征求意见常态化', '2008年10月23日至28日，十一届全国人大常委会举行第五次会议。根据委员长会议的决定，从本次会议开始，常委会审议的法律草案，除不宜公开的以外，原则上都要全文向社会公布，这是不断扩大公民有序参与立法的又一项重要举措。', '5', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('166', '11', '0', null, null, null, null, 'Img/Image_Main/11-06.jpg', null, null, '制定食品安全法', '2009年2月28日，十一届全国人大常委会第七次会议通过食品安全法。', '6', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('167', '11', '0', null, null, null, null, 'Img/Image_Main/11-07.jpg', null, null, '监督重大公共投资项目安排及实施情况', '2009年10月27日至31日，十一届全国人大常委会举行第十一次会议，听取审议全国人大常委会专题调研组关于部分重大公共投资项目实施情况的调研报告。图为部分政府重大公共投资项目专题调研组第一次全体会议。', '7', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('168', '11', '0', null, null, null, null, 'Img/Image_Main/11-08.jpg', null, null, '开展专题询问', '2010年12月24日，十一届全国人大常委会第十八次会议召开联组会议，专题询问国务院关于深化医药卫生体制改革情况。', '14', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('169', '11', '0', null, null, null, null, 'Img/Image_Main/11-09.jpg', null, null, '修改选举法，实现城乡同比', '2010年3月14日，十一届全国人大三次会议通过修改选举法的决定，首次实现城乡同比。图为王兆国副委员长作关于选举法修正案草案的说明。', '10', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('170', '11', '0', null, null, null, null, 'Img/Image_Main/11-10.jpg', null, null, '制定社会保险法', '2010年10月28日，十一届全国人大常委会第十七次会议通过社会保险法。图为广东省从化市社保中心服务大厅。', '12', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('171', '11', '0', null, null, null, null, 'Img/Image_Main/11-11.jpg', null, null, '通过刑法修正案（八）', '2011年2月25日，十一届全国人大常委会第十九次会议通过刑法修正案（八）。', '16', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('172', '11', '0', null, null, null, null, 'Img/Image_Main/11-12.jpg', null, null, '宣布法律体系形成', '2011年3月10日，十一届全国人大四次会议举行第二次全体会议，吴邦国宣布中国特色社会主义法律体系形成。', '17', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('173', '11', '0', null, null, null, null, 'Img/Image_Main/11-13.jpg', null, null, '修改刑事诉讼法', '2012年3月14日，十一届全国人大五次会议通过关于修改刑事诉讼法的决定。', '19', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('174', '11', '0', null, null, null, null, 'Img/Image_Main/11-14.jpg', null, null, '制定加强网络信息保护决定', '2012年12月28日，十一届全国人大常委会第三十次会议通过关于加强网络信息保护的决定。', '23', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('175', '11', '0', null, null, null, null, 'Img/Image_Main/11-15.jpg', null, null, '中国共产党第十八次全国代表大会', '2012年11月8日至14日，中国共产党第十八次全国代表大会在北京举行。胡锦涛在报告中强调，要支持和保证人民通过人民代表大会行使国家权力，全面推进依法治国，完善中国特色社会主义法律体系。', '21', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('176', '11', '0', null, null, null, null, 'Img/Image_Main/11-16.jpg', null, null, '纪念现行宪法公布施行30周年', '2012年12月4日，首都各界纪念宪法公布施行30周年，习近平总书记发表重要讲话。', '22', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('177', '11', '0', null, null, null, null, 'Img/Image_Main/11-17.jpg', null, null, '制定侵权责任法', '2009年12月26日，十一届全国人大常委会第十二次会议通过侵权责任法。', '9', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('178', '11', '0', null, null, null, null, 'Img/Image_Main/11-18.jpg', null, null, '制定行政强制法', '2011年6月30日，十一届全国人大常委会第二十一次会议通过行政强制法。', '18', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('179', '11', '0', null, null, null, null, 'Img/Image_Main/11-19.jpg', null, null, '制定精神卫生法', '2012年10月26日，十一届全国人大常委会第二十九次会议通过精神卫生法。', '20', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('180', '11', '0', null, null, null, null, 'Img/Image_Main/11-20.jpg', null, null, '纪念地方人大设立常委会30周年座谈会', '2009年12月21日，全国人大常委会在京举行纪念地方人大设立常委会30周年座谈会。', '8', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('181', '11', '0', null, null, null, null, 'Img/Image_Main/11-21.jpg', null, null, '中国特色社会主义法律体系座谈会', '2010年8月29日，全国人大常委会举行中国特色社会主义法律体系座谈会。李建国副委员长兼秘书长出席座谈会并讲话，强调要抓紧做好各项工作，确保中国特色社会主义法律体系如期形成。', '11', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('182', '12', '0', null, null, null, null, 'Img/Image_Main/12-01.jpg', null, null, '十二届全国人大常委会委员长张德江', '', '1', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('183', '12', '0', null, null, null, null, 'Img/Image_Main/12-02.jpg', null, null, '十二届全国人大一次会议', '2013年3月5日至17日，十二届全国人大一次会议在北京举行。会议的议程是：听取和审议政府工作报告；审查和批准2012年计划执行情况与2013年计划草案的报告，批准2013年国民经济和社会发展计划；审查和批准2012年预算执行情况与2013年预算草案的报告，批准2013年中央预算；听取和审议全国人大常委会工作报告；听取和审议最高人民法院工作报告；听取和审议最高人民检察院工作报告；听取和审议国务院机构改革和职能转变方案；选举决定国家机构领导人员等。', '2', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('184', '12', '0', null, null, null, null, 'Img/Image_Main/12-03.jpg', null, null, '十二届常委会第一次会议', '2013年3月19日，十二届全国人大常委会举行第一次会议。', '4', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('185', '12', '0', null, null, null, null, 'Img/Image_Main/12-04.jpg', null, null, '制定旅游法', '2013年4月25日，十二届全国人大常委会第二次会议通过旅游法。', '6', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('186', '12', '0', null, null, null, null, 'Img/Image_Main/12-05.jpg', null, null, '制定特种设备安全法', '2013年6月29日，十二届全国人大常委会第三次会议通过特种设备安全法。', '8', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('187', '12', '0', null, null, null, null, 'Img/Image_Main/12-06.jpg', null, null, '通过授权决定，支持自由贸易区试验', '2013年8月30日，十二届全国人大常委会第四次会议通过《关于授权国务院在中国（上海）自由贸易试验区暂时调整有关法律规定的行政审批的决定》。', '10', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('188', '12', '0', null, null, null, null, 'Img/Image_Main/12-07.jpg', null, null, '开展专题询问，提高质量和实效', '2013年8月29日，十二届全国人大常委会第四次会议举行联组会议，专题询问传染病防治工作和传染病防治法实施情况，张德江委员长主持会议。', '9', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('189', '12', '0', null, null, null, null, 'Img/Image_Main/12-08.jpg', null, null, '修改消费者权益保护法', '2013年10月25日，十二届全国人大常委会第五次会议通过修改消费者权益保护法的决定。', '16', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('190', '12', '0', null, null, null, null, 'Img/Image_Main/12-09.jpg', null, null, '党的十八届三中全会强调：推动人大制度与时俱进', '2013年11月9日至12日，中共十八届三中全会在北京举行。中央委员会总书记习近平作重要讲话，会议通过《中共中央关于全面深化改革若干重大问题的决定》。决定指出，要从推进国家治理体系和治理能力现代化的高度，推动人民代表大会制度与时俱进，推进人民代表大会制度理论和实践创新，发挥人民代表大会制度的根本政治制度作用。', '20', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('191', '12', '0', null, null, null, null, 'Img/Image_Main/12-10.jpg', null, null, '建立常委会组成人员联系代表制度', '2013年12月16日，十二届全国人大常委会委员长会议第十四次会议通过全国人大常委会委员长会议组成人员联系全国人大代表的意见（试行）。图为2014年9月11日，张德江委员长在贵州省贵阳花溪区青岩镇人大机关，就如何坚持完善发展好人民代表大会制度等问题，与基层人大代表座谈，他直接联系的两位贵州全国人大代表全程参加了调研。', '21', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('192', '12', '0', null, null, null, null, 'Img/Image_Main/12-11.jpg', null, null, '加强新闻宣传工作', '2013年12月19日，全国人大常委会委员长张德江到《中国人大》杂志调研，并召开人大新闻宣传工作座谈会。图为张德江委员长主持座谈会，李建国副委员长、王晨副委员长兼秘书长一同参加。', '22', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('193', '12', '0', null, null, null, null, 'Img/Image_Main/12-12.jpg', null, null, '废止劳教制度', '2013年12月28日，十二届全国人大常委会第六次会议通过关于《关于废止有关劳动教养法律规定的决定》。', '23', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('194', '12', '0', null, null, null, null, 'Img/Image_Main/12-13.jpg', null, null, '调整完善生育政策', '2013年12月28日，十二届全国人大常委会第六次会议通过《关于调整完善生育政策的决议》。', '24', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('195', '12', '0', null, null, null, null, 'Img/Image_Main/12-14.jpg', null, null, '通过有关历史纪念日的决定', '2014年2月27日，十二届全国人大常委会第七次会议通过关于确定中国人民抗日战争胜利纪念日和关于设立南京大屠杀死难者国家公祭日的两个决定。', '26', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('196', '12', '0', null, null, null, null, 'Img/Image_Main/12-15.jpg', null, null, '修改环境保护法', '2014年4月24日，十二届全国人大常委会第八次会议通过新修订的环境保护法。', '28', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('197', '12', '0', null, null, null, null, 'Img/Image_Main/12-16.jpg', null, null, '修改预算法', '2014年8月31日，十二届全国人大常委会第十次会议通过新修订的预算法。', '33', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('198', '12', '0', null, null, null, null, 'Img/Image_Main/12-17.jpg', null, null, '决定设立知识产权法院', '2014年8月31日，十二届全国人大常委会第十次会议通过关于在北京、上海、广州设立知识产权法院的决定。', '34', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('199', '12', '0', null, null, null, null, 'Img/Image_Main/12-18.jpg', null, null, '成立中国人大制度理论研究会', '2014年1月7日，中国人民代表大会制度理论研究会在北京人民大会堂举行成立大会。', '25', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('200', '12', '0', null, null, null, null, 'Img/Image_Main/12-19.jpg', null, null, '关于香港普选问题的决定', '2014年8月31日，十二届全国人大常委会第十次会议通过了关于香港特别行政区行政长官普选问题和2016年立法会产生办法的决定。', '35', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('201', '12', '0', null, null, null, null, 'Img/Image_Main/12-20.jpg', null, null, '李建国出席纪念劳动法颁布二十周年座谈会', '2014年7月4日，纪念《中华人民共和国劳动法》颁布二十周年座谈会在京举行。李建国副委员长出席座谈会。', '31', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('202', '12', '0', null, null, null, null, 'Img/Image_Main/12-21.jpg', null, null, '王晨赴云南检查义务教育法实施情况', '2013年10月28日，王晨副委员长兼秘书长率全国人大常委会义务教育法执法检查组赴云南进行检查，图为王晨正在向景洪市勐养镇中心小学学生询问学习情况。', '17', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('203', '12', '0', null, null, null, null, 'Img/Image_Main/12-22.jpg', null, null, '向巴平措赴广西检查民族区域自治法实施情况', '2013年6月2日至3日，向巴平措副委员长率全国人大常委会“民族区域自治法实施情况”专题调研组到广西崇左调研。', '7', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('204', '12', '0', null, null, null, null, 'Img/Image_Main/12-23.jpg', null, null, '张宝文赴湖南检查气象法实施情况', '2013年9月5日至6日，张宝文副委员长率全国人大常委会气象法执法检查组到湖南娄底检查。', '12', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('205', '12', '0', null, null, null, null, 'Img/Image_Main/12-24.jpg', null, null, '严隽琪赴青海检查义务教育法实施情况', '2013年9月9日至12日，严隽琪副委员长率全国人大常委会义务教育法执法检查组，对青海省贯彻实施义务教育法的情况进行检查。图为严隽琪副委员长向学校了解法律的贯彻落实情况。', '13', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('206', '12', '0', null, null, null, null, 'Img/Image_Main/12-25.jpg', null, null, '艾力更・依明巴海赴广东检查义务教育法实施情况', '2013年9月9日至13日，艾力更・依明巴海副委员长率全国人大常委会义务教育法执法检查组赴广东检查。图为艾力更在惠州市汇报座谈会上讲话。', '14', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('207', '12', '0', null, null, null, null, 'Img/Image_Main/12-26.jpg', null, null, '吉炳轩赴广东检查专利法实施情况', '2014年4月16日，全国人大常委会副委员长吉炳轩率执法检查组对广东中山市实施专利法情况进行执法检查。', '27', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('208', '12', '0', null, null, null, null, 'Img/Image_Main/12-27.jpg', null, null, '陈昌智赴贵州调研非公有制经济', '2013年11月4日至5日，全国人大常委会副委员长陈昌智到贵州调研非公有制经济发展情况。', '19', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('209', '12', '0', null, null, null, null, 'Img/Image_Main/12-28.jpg', null, null, '张平赴青海甘肃调研统筹推进城乡社保体系建设情况', '2014年7月14日至18日，全国人大常委会张平副委员长率调研组赴青海、甘肃两省进行专题调研。', '32', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('210', '12', '0', null, null, null, null, 'Img/Image_Main/12-29.jpg', null, null, '王胜俊赴河北检查行政复议法实施情况', '2013年9月4日至7日，全国人大常委会副委员长王胜俊率领的全国人大常委会行政复议法执法检查组在辽宁省进行检查。', '11', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('211', '12', '0', null, null, null, null, 'Img/Image_Main/12-30.jpg', null, null, '沈跃跃赴河北调研大气污染防治法', '2014年6月3日，全国人大常委会副委员长沈跃跃赴河北开展大气污染防治法执法检查。', '30', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('222', '11', '0', null, null, null, null, 'Img/Image_Main/11-22.jpg', null, null, '第十六次全国地方立法研讨会', '2010年11月11日至12日，第十六次全国地方立法研讨会在湖北省武汉市召开。王兆国副委员长就形成中国特色社会主义法律体系几个重要问题发表讲话。', '13', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('223', '11', '0', null, null, null, null, 'Img/Image_Main/11-23.jpg', null, null, '形成中国特色社会主义法律体系座谈会', '2011年1月24日，全国人大常委会召开的形成中国特色社会主义法律体系座谈会在北京人民大会堂隆重举行。吴邦国委员长出席座谈会并发表重要讲话。', '15', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('224', '0', '0', null, null, null, null, 'Img/Image_Main/00-21.jpg', null, null, '各地人民代表会议', '1949年10月1日，哈尔滨市召开各界人民代表会议第一次会议。各界人民代表会议历时五年零三个月，为哈尔滨市人民代表大会的确立准备了条件，奠定了基础。图为1950年10月20日，哈尔滨市召开第二届各界人民代表会议第二次会议和当时的代表证。', '11', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('225', '6', '0', null, null, null, null, 'Img/Image_Main/06-19.jpg', null, null, '全国人大会议首次举行记者招待会', '1983年6月4日，六届全国人大一次会议发言人、大会副秘书长曾涛在中外记者招待会上宣布，六届全国人大一次会议预期进行约17天。这是全国人大会议首次举行记者招待会。', '2', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('226', '5', '0', null, null, null, null, 'Img/Image_Main/05-17.jpg', null, null, '成立宪法修改委员会', '1980年9月10日，五届全国人大三次会议通过《关于修改宪法和成立宪法修改委员会的决议》，宪法修改委员会由叶剑英任主任委员，宋庆龄、彭真任副主任委员，万里、习仲勋、王震等103人为委员。', '11', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('227', '12', '0', null, null, null, null, 'Img/Image_Main/12-31.jpg', null, null, '万鄂湘赴甘肃检查未成年人保护法实施情况', '2014年5月19日至23日，全国人大常委会副委员长万鄂湘赴甘肃开展未成年人保护法执法检查。', '29', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('228', '12', '0', null, null, null, null, 'Img/Image_Main/12-32.jpg', null, null, '陈竺赴重庆检查义务教育法实施情况', '2013年10月15日，全国人大常委会副委员长陈竺率全国人大常委会义务教育法执法检查组到重庆检查义务教育法实施情况。', '15', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('229', '12', '0', null, null, null, null, 'Img/Image_Main/12-33.jpg', null, null, '庆祝全国人大成立60周年 习近平总书记发表重要讲话', '2014年9月5日，中共中央、全国人大常委会在北京人民大会堂隆重举行庆祝全国人民代表大会成立60周年大会。中共中央总书记、国家主席、中央军委主席习近平出席并发表重要讲话。', '36', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('230', '0', '0', null, null, null, null, 'Img/Image_Main/00-22.jpg', null, null, '制定工会法', '1950年6月28日，中央人民政府委员会第八次会议通过工会法。', '15', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('231', '9', '0', null, null, null, null, 'Img/Image_Main/09-18.jpg', null, null, '修改中外合资经营企业法', '2001年3月15日，九届全国人大四次会议通过《关于修改〈中外合资经营企业法〉的决定》。图为3月9日，全国人大常委会法制工作委员会主任顾昂然在九届全国人大四次会议第三次全体会议上，作关于《中华人民共和国中外合资经营企业法修正案 （草案）》的说明。', '13', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('232', '10', '0', null, null, null, null, 'Img/Image_Main/10-21.jpg', null, null, '全国人大代表培训班', '2005年4月26日，全国人大代表培训班在杭州举行。全国人大常委会副委员长兼秘书长盛华仁出席开班式并讲话。', '9', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('233', '12', '0', null, null, null, null, 'Img/Image_Main/12-35.jpg', null, null, '习近平阐述“中国梦”', '2013年3月17日，习近平主席在十二届全国人大一次会议闭幕会上发表重要讲话。', '3', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('234', '12', '0', null, null, null, null, 'Img/Image_Main/12-36.jpg', null, null, '张德江发表重要讲话', '2013年3月19日，十二届全国人大常委会举行第一次会议。张德江委员长主持会议并发表重要讲话。他强调，要认真学习贯彻党的十八大精神，坚定不移走中国特色社会主义政治发展道路，坚持和完善人民代表大会制度，坚持依法治国，维护宪法和法律的权威，团结一心、开拓前进，推动人大工作迈出新步伐、迈上新台阶。', '5', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('235', '12', '0', null, null, null, null, 'Img/Image_Main/12-34.jpg', null, null, '召开立法工作会议', '2013年10月30日，全国人大常委会立法工作会议在人民大会堂举行。张德江委员长出席会议并讲话。他强调，要深入贯彻党的十八大精神和习近平总书记一系列重要讲话精神，分析我国立法工作面临的新形势，加强和改进立法工作，提高立法质量，落实立法规划，不断完善中国特色社会主义法律体系，为全面建成小康社会、坚持和发展中国特色社会主义、实现中华民族伟大复兴的中国梦提供更加有力的法制保障。', '18', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('236', '2', '0', null, null, null, null, 'Img/Image_Main/02-10.jpg', null, null, '与国外议会交往', '1962年9月30日，朱德委员长接见印尼哈蒂尼・苏加诺夫人。', '10', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('237', '12', '0', null, null, null, null, 'Img/Image_Main/12-37.jpg', null, null, '庆祝全国人大成立60周年理论研讨会', '2014年9月11日，庆祝全国人大成立60周年理论研讨会在京举行，张德江委员长出席会议并发表重要讲话。图为会场场景。', '37', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('238', '3', '0', null, null, null, null, 'Img/Image_Main/03-07.jpg', null, null, '与国外议会交往', '1965年5月30日，应邀前来我国进行友好访问的印度尼西亚合作国国会议长阿鲁季・卡塔威纳塔在北京车站受到朱德委员长、陈毅副总理和首都各界人民的热烈欢迎。', '7', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('239', '4', '0', null, null, null, null, 'Img/Image_Main/04-05.jpg', null, null, '代表活动', '1974年11月10日，四届全国人大代表、国家著名数学家华罗庚及其优选法推广小组，应邀前来广西传授、推广优选法，并在南宁作《关于推广、应用优选法的报告》。图为华罗庚（右一）在广西南宁手扶拖拉机厂与工人讨论推广优选法。', '5', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('240', '4', '0', null, null, null, null, 'Img/Image_Main/04-06.jpg', null, null, '与国外议会交往', '1976年4月19日，朱德委员长在北京会见阿拉伯埃及共和国副总统胡斯尼・穆巴拉克一行。', '6', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('241', '5', '0', null, null, null, null, 'Img/Image_Main/05-18.jpg', null, null, '与国外议会交往', '1978年10月19日，叶剑英委员长、聂荣臻副委员长会见全印柯棣华大夫纪念委员会主席巴苏医生和夫人。', '18', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('242', '6', '0', null, null, null, null, 'Img/Image_Main/06-20.jpg', null, null, '与国外议会交往', '1985年4月24日，彭真委员长在日本国会发表演说。', '20', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('243', '7', '0', null, null, null, null, 'Img/Image_Main/07-17.jpg', null, null, '与国外议会交往', '1989年3月15日，周谷城副委员长在北京人民大会堂会见日本定型诗人代表团团长近藤芳美一行。', '17', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_main` VALUES ('244', '7', '0', null, null, null, null, 'Img/Image_Main/07-18.jpg', null, null, '代表活动', '1991年2月23日，全国人大代表姚峻（前左一）与其他代表一起到天津染化九厂调查，听取汇报。', '18', '0', null, '2014-09-20 12:45:08', null);
INSERT INTO `image_related` VALUES ('1', '4', '0', '2', null, null, null, null, 'Img/Image_Related/00-04-2001.flv', 'Img/Image_Related/00-04-2001.jpg', null, null, '探索政权建设（一）', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('2', '4', '0', '2', null, null, null, null, 'Img/Image_Related/00-04-2002.flv', 'Img/Image_Related/00-04-2002.jpg', null, null, '探索政权建设（二）', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('3', '4', '0', '1', null, null, null, null, 'Img/Image_Related/00-04-1002.jpg', 'Img/Image_Related/00-04-1002.jpg', null, null, '中华苏维埃第一次全国代表大会通过了《中华苏维埃共和国宪法大纲》。右起：王稼祥、毛泽东、项英、邓发、朱德、任弼时在代表大会上。', null, '3', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('4', '4', '0', '1', null, null, null, null, 'Img/Image_Related/00-04-1003.jpg', 'Img/Image_Related/00-04-1003.jpg', null, null, '中华苏维埃共和国临时中央政府旧址――江西瑞金叶坪村谢氏祠堂内景。1931年11月，中华苏维埃第一次全国代表大会结束后，毛泽东被推选为临时中央政府执行委员会主席和中央人民委员会主席。从此，人们开始称毛泽东为“毛主席”。', null, '4', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('5', '4', '0', '1', null, null, null, null, 'Img/Image_Related/00-04-1004.jpg', 'Img/Image_Related/00-04-1004.jpg', null, null, '1931年，中华苏维埃第一次全国代表大会授旗、授章典礼。', null, '5', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('6', '4', '0', '1', null, null, null, null, 'Img/Image_Related/00-04-1005.jpg', 'Img/Image_Related/00-04-1005.jpg', null, null, '1932年3月18日至21日，100余名代表参加了福建省第一次工农兵代表大会。中华苏维埃临时中央政府派代表任弼时出席并指导大会。大会宣告了福建省苏维埃政府的成立。旧址现为长汀县博物馆。', null, '6', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('7', '4', '0', '1', null, null, null, null, 'Img/Image_Related/00-04-1006.jpg', 'Img/Image_Related/00-04-1006.jpg', null, null, '1933年6月17日至7月1日，中央苏区召开了八县区贫农团代表大会。毛泽东在大会上讲话。', null, '7', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('8', '4', '0', '1', null, null, null, null, 'Img/Image_Related/00-04-1007.jpg', 'Img/Image_Related/00-04-1007.jpg', null, null, '我国新民主主义革命时期农民运动的优秀领导人澎湃（1896年－1929年），曾在广东海丰“红宫”召开东江农民代表大会，宣布成立海丰苏维埃政府。这是当年海丰苏维埃政府旧址。', null, '8', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('9', '4', '0', '1', null, null, null, null, 'Img/Image_Related/00-04-1008.jpg', 'Img/Image_Related/00-04-1008.jpg', null, null, '江西吉安市收藏家杨焕明收藏到一枚铜质镀银“吉安农民协会会员”徽章。这是罕见的大革命时期农民运动的历史文物。', null, '9', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('10', '4', '0', '1', null, null, null, null, 'Img/Image_Related/00-04-1009.jpg', 'Img/Image_Related/00-04-1009.jpg', null, null, '1926年10月至1927年3月，上海工人连续举行三次武装起义，并召开上海市民代表大会。', null, '10', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('11', '4', '0', '1', null, null, null, null, 'Img/Image_Related/00-04-1010.jpg', 'Img/Image_Related/00-04-1010.jpg', null, null, '1927年3月湖北第一次农民代表大会在武昌召开，毛泽东当选为大会名誉主席。', null, '11', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('12', '4', '0', '1', null, null, null, null, 'Img/Image_Related/00-04-1011.jpg', 'Img/Image_Related/00-04-1011.jpg', null, null, '这是广州起义时张贴的标语（原物）。', null, '12', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('13', '4', '0', '1', null, null, null, null, 'Img/Image_Related/00-04-1012.jpg', 'Img/Image_Related/00-04-1012.jpg', null, null, '1927年广州起义后建立的广州苏维埃政府和起义军总指挥部所在地。广州起义是继南昌起义、秋收起义之后我党领导的又一次武装起义。起义武装曾占领了市内绝大部分地区，并成立了广州苏维埃政府。', null, '13', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('14', '7', '0', '1', null, null, null, null, 'Img/Image_Related/00-07-1001.jpg', 'Img/Image_Related/00-07-1001.jpg', null, null, '抗日战争时期，延安人民在选举边区参议员。', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('15', '7', '0', '1', null, null, null, null, 'Img/Image_Related/00-07-1002.jpg', 'Img/Image_Related/00-07-1002.jpg', null, null, '1943年，谢觉哉在延安。当时，他任陕甘宁边区参议会副议长，兼管司法工作，主持制定了婚姻条例等法律。', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('16', '8', '0', '2', null, null, null, null, 'Img/Image_Related/00-08-2001.flv', 'Img/Image_Related/00-08-2001.jpg', null, null, '延安“豆选”', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('17', '8', '0', '2', null, null, null, null, 'Img/Image_Related/00-08-1001.jpg', 'Img/Image_Related/00-08-1001.jpg', null, null, '1940年8月晋察冀边区公布《施政纲领》并进行普选。图为边区人民在进行基层政权的选举。', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('18', '10', '0', '2', null, null, null, null, 'Img/Image_Related/00-10-2001.flv', 'Img/Image_Related/00-10-2001.jpg', null, null, '毛泽东提出人民代表大会的构想', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('19', '10', '0', '1', null, null, null, null, 'Img/Image_Related/00-10-1001.jpg', 'Img/Image_Related/00-10-1001.jpg', null, null, '1940年1月，在毛泽东发表的《新民主主义论》中，第一次提出中国可以采取全国人民代表大会、省人民代表大会、县人民代表大会、区人民代表大会、直到乡人民代表大会的系统，并由各级人民代表大会选举政府。左图为该著作的第五部分“新民主主义的政治”的有关内容。右图为最早在延安出版的《新民主主义论》单行本封面。', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('20', '11', '0', '2', null, null, null, null, 'Img/Image_Related/00-11-2001.flv', 'Img/Image_Related/00-11-2001.jpg', null, null, '成立中国人民政治协商会议筹备会，起草《共同纲领》', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('21', '11', '0', '2', null, null, null, null, 'Img/Image_Related/00-11-2002.flv', 'Img/Image_Related/00-11-2002.jpg', null, null, '起草《共同纲领》', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('22', '11', '0', '2', null, null, null, null, 'Img/Image_Related/00-11-2003.flv', 'Img/Image_Related/00-11-2003.jpg', null, null, '投票通过《共同纲领》及相关内容', null, '3', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('23', '11', '0', '2', null, null, null, null, 'Img/Image_Related/00-11-2004.flv', 'Img/Image_Related/00-11-2004.jpg', null, null, '共同纲领相关内容', null, '4', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('24', '11', '0', '2', null, null, null, null, 'Img/Image_Related/00-11-2005.flv', 'Img/Image_Related/00-11-2005.jpg', null, null, '许崇德谈《共同纲领》的意义', null, '5', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('25', '11', '0', '2', null, null, null, null, 'Img/Image_Related/00-11-2006.flv', 'Img/Image_Related/00-11-2006.jpg', null, null, '中国人民政治协商会议第一次全体会议开幕', null, '6', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('26', '11', '0', '2', null, null, null, null, 'Img/Image_Related/00-11-2007.flv', 'Img/Image_Related/00-11-2007.jpg', null, null, '中国人民政治协商会议第一次全体会议开幕', null, '7', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('27', '11', '0', '2', null, null, null, null, 'Img/Image_Related/00-11-2008.flv', 'Img/Image_Related/00-11-2008.jpg', null, null, '中国人民政治协商会议第一次全体会议开幕-毛泽东讲话', null, '8', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('28', '11', '0', '2', null, null, null, null, 'Img/Image_Related/00-11-2009.flv', 'Img/Image_Related/00-11-2009.jpg', null, null, '中国人民政治协商会议第一次全体会议开幕-毛泽东宣布开幕', null, '9', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('29', '11', '0', '2', null, null, null, null, 'Img/Image_Related/00-11-2010.flv', 'Img/Image_Related/00-11-2010.jpg', null, null, '中国政治协商筹备会议第二次全体会议', null, '10', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('30', '11', '0', '2', null, null, null, null, 'Img/Image_Related/00-11-2011.flv', 'Img/Image_Related/00-11-2011.jpg', null, null, '中国政治协商筹备会议第一次全体会议', null, '11', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('31', '11', '0', '2', null, null, null, null, 'Img/Image_Related/00-11-2012.flv', 'Img/Image_Related/00-11-2012.jpg', null, null, '中国政治协商筹备会议第一次全体会议开幕', null, '12', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('32', '11', '0', '2', null, null, null, null, 'Img/Image_Related/00-11-2013.flv', 'Img/Image_Related/00-11-2013.jpg', null, null, '中国人民政治协商会议第一次全体会议闭幕-朱德致辞', null, '13', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('33', '11', '0', '2', null, null, null, null, 'Img/Image_Related/00-11-2014.flv', 'Img/Image_Related/00-11-2014.jpg', null, null, '中国人民政治协商会议第一次全体会议-通过了中央人民政府组织法和中国人民政治协商会议组织法等相关事宜', null, '14', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('34', '11', '0', '2', null, null, null, null, 'Img/Image_Related/00-11-2015.flv', 'Img/Image_Related/00-11-2015.jpg', null, null, '中国人民政治协商会议第一次全体会议-投票选举了中央人民政府，选举毛泽东为中央人民政府主席', null, '15', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('35', '11', '0', '2', null, null, null, null, 'Img/Image_Related/00-11-2016.flv', 'Img/Image_Related/00-11-2016.jpg', null, null, '中国人民政治协商会议第一次全体会议-选举产生了中国人民政治协商会议委员会和中央人民政府委员会', null, '16', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('36', '11', '0', '1', null, null, null, null, 'Img/Image_Related/00-11-1004.jpg', 'Img/Image_Related/00-11-1004.jpg', null, null, '1949年9月21日至30日，中国人民政治协商会议第一届全体会议在北平中南海怀仁堂举行。图为会场外景。', null, '17', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('37', '11', '0', '1', null, null, null, null, 'Img/Image_Related/00-11-1001.jpg', 'Img/Image_Related/00-11-1001.jpg', null, null, '1949年9月21日，毛泽东在中国人民政治协商会议第一届全体会议上致开幕词，庄严宣告：“占人类总人数四分之一的中国人从此站立起来了。”', null, '18', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('38', '11', '0', '1', null, null, null, null, 'Img/Image_Related/00-11-1002.jpg', 'Img/Image_Related/00-11-1002.jpg', null, null, '1949年9月，周恩来在中国人民政治协商会议第一届全体会议上讲话。受中共中央委派，周恩来主持了《共同纲领》的起草工作，毛泽东多次审阅并进行了修改。', null, '19', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('39', '11', '0', '1', null, null, null, null, 'Img/Image_Related/00-11-1003.jpg', 'Img/Image_Related/00-11-1003.jpg', null, null, '1949年9月，董必武在中国人民政治协商会议第一届全体会议上，作《关于中华人民共和国中央人民政府组织法草案的特点的报告》。', null, '20', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('40', '12', '0', '1', null, null, null, null, 'Img/Image_Related/00-12-1001.jpg', 'Img/Image_Related/00-12-1001.jpg', null, null, '1950年6月28日，毛泽东主席在中央人民政府委员会第八次会议上主持讨论中华人民共和国国徽图案。', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('41', '12', '0', '1', null, null, null, null, 'Img/Image_Related/00-12-1002.jpg', 'Img/Image_Related/00-12-1002.jpg', null, null, '中国人民政治协商会议第一届全国委员会第二次会议于1950年6月14日至23日在北京举行。会议同意国徽审查组代表马叙伦关于国徽图案审查意见的报告，并通过了国徽图案，建议中央人民政府委员会采用。特邀列席人士起义将领（左起）卢汉、邓锡侯、刘文辉在前排就座。', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('42', '12', '0', '1', null, null, null, null, 'Img/Image_Related/00-12-1003.jpg', 'Img/Image_Related/00-12-1003.jpg', null, null, '中国人民政治协商会议第一届全国委员会第二次会议通过的中华人民共和国国徽草案。', null, '3', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('43', '12', '0', '1', null, null, null, null, 'Img/Image_Related/00-12-1004.jpg', 'Img/Image_Related/00-12-1004.jpg', null, null, '1949年6月15日至19日召开的中国人民政治协商会议筹备会从2992幅国旗图案中精选出38幅草图，编成《国旗图案参考资料》，提请中国人民政治协商会议第一届全体会议讨论。右为曾联松设计的一幅。', null, '4', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('44', '12', '0', '1', null, null, null, null, 'Img/Image_Related/00-12-1005.jpg', 'Img/Image_Related/00-12-1005.jpg', null, null, '这是当年中央人民政府委员会办公厅给曾联松的信。信上写道：“曾联松先生：你所设计的中华人民共和国国旗，业已采用，兹赠送人民政协纪念刊一册，人民币五百万元（相当于现在人民币五百元），作为酬谢你对国家的贡献，并致深切的敬意……。', null, '5', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('45', '13', '0', '1', null, null, null, null, 'Img/Image_Related/00-13-1001.jpg', 'Img/Image_Related/00-13-1001.jpg', null, null, '1949年10月1日，30万军民参加了开国大典。中华人民共和国的建立结束了半殖民地半封建的旧中国历史，开辟了中国历史的新纪元。', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('46', '13', '0', '1', null, null, null, null, 'Img/Image_Related/00-13-1002.jpg', 'Img/Image_Related/00-13-1002.jpg', null, null, '1949年10月1日，下午3时北京30万人在天安门广场隆重举行开国典礼，庆祝中华人民共和国中央人民政府成立。（放在开国大典）', null, '3', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('47', '14', '0', '1', null, null, null, null, 'Img/Image_Related/00-14-1001.jpg', 'Img/Image_Related/00-14-1001.jpg', null, null, '1950年6月30日，中央人民政府颁布了《中华人民共和国土地改革法》，受到农民的热烈拥护。这是河南省洛阳专区偃师县马', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('48', '14', '0', '1', null, null, null, null, 'Img/Image_Related/00-14-1002.jpg', 'Img/Image_Related/00-14-1002.jpg', null, null, '1950年6月30日，《中华人民共和国土地改革法》颁布后江西瑞金地区工作队向农民开展宣传。', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('49', '15', '0', '1', null, null, null, null, 'Img/Image_Related/00-15-1001.jpg', 'Img/Image_Related/00-15-1001.jpg', null, null, '1953年2月11日，中央人民政府委员会举行第二十二次会议。会上由中华人民共和国选举法起草委员会委员邓小平(右第一人)作《中华人民共和国全国人民代表大会给及地方各级人民代表大会选举法》的说明报告。主席台上: (左起)林伯渠、宋庆龄、毛泽东、刘少奇、朱德、李济深、张澜、邓小平。', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('50', '16', '0', '1', null, null, null, null, 'Img/Image_Related/00-16-1001.jpg', 'Img/Image_Related/00-16-1001.jpg', null, null, '1953年，广州市珠海区黄沙段水上居民在选区投票站投票选举人大代表。', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('51', '16', '0', '1', null, null, null, null, 'Img/Image_Related/00-16-1002.jpg', 'Img/Image_Related/00-16-1002.jpg', null, null, '1953年，革命圣地延安县的普选全面展开，全县8万多人民怀着无比兴奋的心情参加普选。', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('52', '16', '0', '1', null, null, null, null, 'Img/Image_Related/00-16-1003.jpg', 'Img/Image_Related/00-16-1003.jpg', null, null, '山西省平顺县西沟乡西沟区全国劳动模范李顺达、申纪兰通过选举当选为西沟乡人民代表。（左起）申纪兰、李顺达、韩春兰和张秋全在观看当选证书。', null, '3', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('53', '16', '0', '1', null, null, null, null, 'Img/Image_Related/00-16-1004.jpg', 'Img/Image_Related/00-16-1004.jpg', null, null, '1953年4月，延安县的普选全面展开。图为桥镇乡桥儿沟的选民进入选举会场。', null, '4', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('54', '16', '0', '1', null, null, null, null, 'Img/Image_Related/00-16-1005.jpg', 'Img/Image_Related/00-16-1005.jpg', null, null, '守卫朝鲜某地的中国人民志愿军在投票选举基层人民代表大会的代表。', null, '5', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('55', '16', '0', '1', null, null, null, null, 'Img/Image_Related/00-16-1006.jpg', 'Img/Image_Related/00-16-1006.jpg', null, null, '延安县南关乡第五选区的一个机关选民小组正在访问代表候选人李生荣（右第三人）。', null, '6', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('56', '224', '0', '1', null, null, null, null, 'Img/Image_Related/00-16-1007.jpg', 'Img/Image_Related/00-16-1007.jpg', null, null, '1953年10月25日至30日，宁夏固原县召开西海固回族自治区各族各界人民代表会议，会议代行人民代表大会职权，选出自治区人民政府主席、副主席和委员，正式成立了西海固回族自治区人民政府。这是参加大会的回族老人。', null, '7', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('57', '16', '0', '1', null, null, null, null, 'Img/Image_Related/00-16-1008.jpg', 'Img/Image_Related/00-16-1008.jpg', null, null, '1953年12月，山西省太原钢铁厂的职工，积极地参加普选。', null, '8', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('58', '16', '0', '1', null, null, null, null, 'Img/Image_Related/00-16-1009.jpg', 'Img/Image_Related/00-16-1009.jpg', null, null, '1953年12月6日，北京市西单区的选民兴高采烈地参加投票选举人大代表。', null, '9', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('59', '16', '0', '1', null, null, null, null, 'Img/Image_Related/00-16-1010.jpg', 'Img/Image_Related/00-16-1010.jpg', null, null, '河南省杞县七区云所乡农民在投票选举代表。', null, '10', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('60', '224', '0', '1', null, null, null, null, 'Img/Image_Related/00-16-1011.jpg', 'Img/Image_Related/00-16-1011.jpg', null, null, '1954年7月新疆省南部克兹勒苏柯尔克兹自治区举行首届第一次人民代表会议。会议代行人民代表大会职权，选出了自治区人民政府主席、副主席和委员，成立了克兹勒苏柯尔克兹自治区人民政府。', null, '11', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('61', '17', '0', '2', null, null, null, null, 'Img/Image_Related/00-17-2001.flv', 'Img/Image_Related/00-17-2001.jpg', null, null, '人民代表会议-如何建立地方政权', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('62', '17', '0', '2', null, null, null, null, 'Img/Image_Related/00-17-2002.flv', 'Img/Image_Related/00-17-2002.jpg', null, null, '各地人民代表会议（一）', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('63', '17', '0', '2', null, null, null, null, 'Img/Image_Related/00-17-2003.flv', 'Img/Image_Related/00-17-2003.jpg', null, null, '各地人民代表会议（二）', null, '3', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('64', '18', '0', '1', null, null, null, null, 'Img/Image_Related/00-18-1001.jpg', 'Img/Image_Related/00-18-1001.jpg', null, null, '1953年，新民主主义青年团中央团校少数民族班学生在倾听婚姻法的讲解。', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('65', '18', '0', '1', null, null, null, null, 'Img/Image_Related/00-18-1002.jpg', 'Img/Image_Related/00-18-1002.jpg', null, null, '1951年，北京市民主妇联在一对农民结婚典礼上宣传婚姻法。', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('66', '18', '0', '1', null, null, null, null, 'Img/Image_Related/00-18-1003.jpg', 'Img/Image_Related/00-18-1003.jpg', null, null, '婚姻法颁布后，婚姻已由当事人自己做主。清末到中华民国初期，这种便条式婚书在普通居民中非常流行。这是山西汾城县一对夫妇在1950年结婚时，仍然使用这种婚书。', null, '3', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('67', '19', '0', '1', null, null, null, null, 'Img/Image_Related/00-19-1001.jpg', 'Img/Image_Related/00-19-1001.jpg', null, null, '1954年6月11日，中华人民共和国宪法起草委员会在北京召开第七次全体会议。毛泽东在会上讲话。图为（左起）朱德、毛泽东、刘少奇、李济深。', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('68', '20', '0', '1', null, null, null, null, 'Img/Image_Related/00-20-1001.jpg', 'Img/Image_Related/00-20-1001.jpg', null, null, '1954年6月，北京国营第一棉纺厂的工人们，用黑板报宣传《中华人民共和国宪法草案》。', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('69', '20', '0', '1', null, null, null, null, 'Img/Image_Related/00-20-1002.jpg', 'Img/Image_Related/00-20-1002.jpg', null, null, '新疆第一届人民代表大会第一次会议于1954年7月下旬在乌鲁木齐市举行， 这是出席会议的各族代表在热烈讨论宪法草案。', null, '3', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('70', '20', '0', '1', null, null, null, null, 'Img/Image_Related/00-20-1003.jpg', 'Img/Image_Related/00-20-1003.jpg', null, null, '1954年6月，西安市一家清真寺的阿訇（右拿报纸者）在向回族群众宣传《中华人民共和国宪法草案》。', null, '4', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('71', '20', '0', '1', null, null, null, null, 'Img/Image_Related/00-20-1004.jpg', 'Img/Image_Related/00-20-1004.jpg', null, null, '1954年6月，《中华人民共和国宪法草案》由民族出版社翻译成蒙、藏、维吾尔、哈萨克和朝鲜五种文字，草案在全国展开广泛讨论。', null, '5', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('72', '20', '0', '1', null, null, null, null, 'Img/Image_Related/00-20-1005.jpg', 'Img/Image_Related/00-20-1005.jpg', null, null, '1954年，广州市华侨招待所的青年归侨在阅读《中华人民共和国宪法草案》。', null, '6', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('73', '20', '0', '1', null, null, null, null, 'Img/Image_Related/00-20-1006.jpg', 'Img/Image_Related/00-20-1006.jpg', null, null, '1949年9月22日，筹备会第三小组组长周恩来在政协第一届全体会议上作《关于草拟中国人民政治协商会议共同纲领的经过及其特点的报告》。 右下图为1949年9月29日政协第一届全体会议通过的《中国人民政治协商会议共同纲领》。', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('74', '22', '1', '2', null, null, null, null, 'Img/Image_Related/01-22-2001.flv', 'Img/Image_Related/01-22-2001.jpg', null, null, '筹备召开第一届全国人民代表大会，成立宪法起草委员会', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('75', '22', '1', '2', null, null, null, null, 'Img/Image_Related/01-22-2002.flv', 'Img/Image_Related/01-22-2002.jpg', null, null, '新中国成立以来第一次普选基层人大代表', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('76', '22', '1', '2', null, null, null, null, 'Img/Image_Related/01-22-2003.flv', 'Img/Image_Related/01-22-2003.jpg', null, null, '毛泽东、朱德、刘少奇、周恩来等党和国家领导参加投票选举基层人大代表', null, '3', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('77', '22', '1', '2', null, null, null, null, 'Img/Image_Related/01-22-2004.flv', 'Img/Image_Related/01-22-2004.jpg', null, null, '第一届全国人民代表大会开幕-代表入场', null, '4', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('78', '22', '1', '2', null, null, null, null, 'Img/Image_Related/01-22-2005.flv', 'Img/Image_Related/01-22-2005.jpg', null, null, '周恩来在第一届全国人民代表大会第一次会议上作政府工作报告', null, '5', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('79', '22', '1', '1', null, null, null, null, 'Img/Image_Related/01-22-1001.jpg', 'Img/Image_Related/01-22-1001.jpg', null, null, '蔡畅在一届全国人大一次会议上发言。', null, '6', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('80', '22', '1', '1', null, null, null, null, 'Img/Image_Related/01-22-1002.jpg', 'Img/Image_Related/01-22-1002.jpg', null, null, '出席一届全国人大一次会议的几位女代表。（从左到右第二人起）：邓颖超　（汉族）、赛力玛・塔力甫瓦（维吾尔族）、黄莲辉（僮族）、蒙素芬（布依族）、李桂英（彝族）、黎明（僮族）。', null, '7', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('81', '22', '1', '1', null, null, null, null, 'Img/Image_Related/01-22-1003.jpg', 'Img/Image_Related/01-22-1003.jpg', null, null, '出席一届全国人大一次会议的代表  （左起）华罗庚、老舍、梁思成、梅兰芳在会议休息时交谈。', null, '8', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('82', '22', '1', '1', null, null, null, null, 'Img/Image_Related/01-22-1004.jpg', 'Img/Image_Related/01-22-1004.jpg', null, null, '出席一届全国人大一次会议的上海市工商界代表在会议休息时交谈。自左至右：汤蒂因、荣毅仁、胡厥文、王志莘。', null, '9', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('83', '22', '1', '1', null, null, null, null, 'Img/Image_Related/01-22-1005.jpg', 'Img/Image_Related/01-22-1005.jpg', null, null, '出席一届全国人大一次会议的几位女代表。自左至右：胡文秀、吴志珍（苗族）、蒙素芬（布依族）、常香玉、绕西・泽仁卓玛（藏族）、申纪兰、裔式娟。', null, '10', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('84', '22', '1', '1', null, null, null, null, 'Img/Image_Related/01-22-1006.jpg', 'Img/Image_Related/01-22-1006.jpg', null, null, '出席一届全国人大一次会议的几位农民代表。从右到左：梁军、李顺达、邓国章、吴春安、苏殿选。', null, '11', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('85', '22', '1', '1', null, null, null, null, 'Img/Image_Related/01-22-1007.jpg', 'Img/Image_Related/01-22-1007.jpg', null, null, '1954年9月，全国人大代表、内蒙古自治区人民政府主席乌兰夫（右二）深入牧区访问。', null, '12', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('86', '22', '1', '1', null, null, null, null, 'Img/Image_Related/01-22-1008.jpg', 'Img/Image_Related/01-22-1008.jpg', null, null, '一届全国人民代表大会一次会议出席证', null, '13', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('87', '23', '1', '2', null, null, null, null, 'Img/Image_Related/01-23-2001.flv', 'Img/Image_Related/01-23-2001.jpg', null, null, '第一届全国人民代表大会开幕-毛泽东致开幕词。', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('88', '23', '1', '1', null, null, null, null, 'Img/Image_Related/01-23-1001.jpg', 'Img/Image_Related/01-23-1001.jpg', null, null, '1954年9月，毛泽东和参加一届全国人大的代表们在一起。', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('89', '23', '1', '1', null, null, null, null, 'Img/Image_Related/01-23-1002.jpg', 'Img/Image_Related/01-23-1002.jpg', null, null, '毛泽东主席和青海省全国人大代表、中国佛教协会代理会长喜饶嘉措在一届全国人大一次会议上交谈。', null, '3', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('90', '23', '1', '1', null, null, null, null, 'Img/Image_Related/01-23-1003.jpg', 'Img/Image_Related/01-23-1003.jpg', null, null, '1958年6月28日，毛泽东主席亲切接见新疆和阗县人大代表八十多岁的老人库尔班・吐鲁木。', null, '4', '0', null, '2014-09-22 12:17:04', null);
