INSERT INTO `image_related` VALUES ('146', '236', '2', '1', null, null, null, null, 'Img/Image_Related/02-37-1004.jpg', 'Img/Image_Related/02-37-1004.jpg', null, null, '朱德委员长1959年6月9日在旅大接见了秘鲁议员团。图为朱德委员长给客人签名留念。', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('147', '236', '2', '1', null, null, null, null, 'Img/Image_Related/02-37-1005.jpg', 'Img/Image_Related/02-37-1005.jpg', null, null, '以林伯渠副委员长（前右一）为团长、黄炎培副委员长（前右二）为副团长的全国人民代表大会代表团1959年8月26日离开北京前往乌兰巴托，访问蒙古人民共和国。', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('148', '236', '2', '1', null, null, null, null, 'Img/Image_Related/02-37-1006.jpg', 'Img/Image_Related/02-37-1006.jpg', null, null, '1961年10月8日，李维汉副委员长设宴欢送以匈牙利议会副主席瓦什・伊斯特万妮为首的匈牙利国民议会代表团。', null, '3', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('149', '236', '2', '1', null, null, null, null, 'Img/Image_Related/02-37-1008.jpg', 'Img/Image_Related/02-37-1008.jpg', null, null, '1962年9月30日，彭真副委员长率中国人大代表团访问越南时，与越南劳动党中央委员会主席、越南民主共和国主席胡志明亲切交谈。', null, '4', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('150', '236', '2', '1', null, null, null, null, 'Img/Image_Related/02-37-1009.jpg', 'Img/Image_Related/02-37-1009.jpg', null, null, '1963年10月，朱德委员长在首都机场欢送尼泊尔王国全国评议会议长比什瓦・班杜・塔帕和夫人以及由塔帕议长率领的尼泊尔王国全国评议会代表团。', null, '5', '0', null, '2014-09-22 12:17:04', null);
