INSERT INTO `image_related` VALUES ('256', '66', '5', '1', null, null, null, null, 'Img/Image_Related/05-66-1003.jpg', 'Img/Image_Related/05-66-1003.jpg', null, null, '1982年11月26日至12月10日，五届全国人大五次会议在北京举行。这是11月26日彭真（中）、徐向前（右）、乌兰夫步入大会会场。', null, '7', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('257', '64', '5', '1', null, null, null, null, 'Img/Image_Related/05-66-1004.jpg', 'Img/Image_Related/05-66-1004.jpg', null, null, '1982年12月4日，叶剑英委员长在流动票箱投票表决宪法。', null, '2', '0', null, '2014-09-22 12:17:04', null);
