INSERT INTO `image_related` VALUES ('259', '66', '5', '1', null, null, null, null, 'Img/Image_Related/05-66-1006.jpg', 'Img/Image_Related/05-66-1006.jpg', null, null, '吉林省两位年逾花甲的回族老人马延增（左）和回宝珍，在一起阅读《中华人民共和国宪法修改草案》。', null, '10', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('260', '60', '5', '1', null, null, null, null, 'Img/Image_Related/05-66-1007.jpg', 'Img/Image_Related/05-66-1007.jpg', null, null, '内蒙古自治区那仁宝力格公社书记官布苏荣（右二）在向干部、牧民介绍《中华人民共和国宪法修改草案》。', null, '1', '0', null, '2014-09-22 12:17:04', null);
