INSERT INTO `image_related` VALUES ('274', '72', '6', '1', null, null, null, null, 'Img/Image_Related/06-72-1001.jpg', 'Img/Image_Related/06-72-1001.jpg', null, null, '1983年6月6日至21日，六届全国人大一次会议在北京举行。这是第六届全国人民代表大会第一次会议上当选为中华人民共和国主席的李先念同当选为全国人大常委会委员长的彭真在一起。', null, '10', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('275', '72', '6', '1', null, null, null, null, 'Img/Image_Related/06-72-1002.jpg', 'Img/Image_Related/06-72-1002.jpg', null, null, '1983年6月18日，在六届全国人大一次会议上，班禅额尔德尼・确吉坚赞投票选举国家领导人。', null, '9', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('276', '72', '6', '1', null, null, null, null, 'Img/Image_Related/06-72-1003.jpg', 'Img/Image_Related/06-72-1003.jpg', null, null, '出席六届全国人大一次会议的邓小平和陈云（左）在会议休息时亲切交谈。', null, '3', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('277', '72', '6', '1', null, null, null, null, 'Img/Image_Related/06-72-1004.jpg', 'Img/Image_Related/06-72-1004.jpg', null, null, '出席六届全国人大一次会议的陈云和彭真（右）在会议休息时亲切交谈。', null, '4', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('278', '72', '6', '1', null, null, null, null, 'Img/Image_Related/06-72-1005.jpg', 'Img/Image_Related/06-72-1005.jpg', null, null, '出席六届全国人大一次会议的徐向前和薄一波在会议休息时亲切交谈。', null, '5', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('279', '72', '6', '1', null, null, null, null, 'Img/Image_Related/06-72-1006.jpg', 'Img/Image_Related/06-72-1006.jpg', null, null, '1983年1月1日，全国人大常委会副委员长乌兰夫、阿沛・阿旺晋美、班禅额尔德尼・确吉坚赞，全国政协委员会副主席刘澜涛、包尔汉，中共中央统战部部长杨静仁等出席了在民族文化宫举行的新年茶话会。', null, '7', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('280', '72', '6', '1', null, null, null, null, 'Img/Image_Related/06-72-1007.jpg', 'Img/Image_Related/06-72-1007.jpg', null, null, ' 1983年9月29日，叶飞副委员长（前排中）会见了参加国庆活动的华侨、华人观光团。全国人大华侨委员会副主任委员何英（前右）、国务院侨务办公室副主任林修德（前左）参加了会见。', null, '11', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('281', '71', '6', '1', null, null, null, null, 'Img/Image_Related/06-72-1008.jpg', 'Img/Image_Related/06-72-1008.jpg', null, null, '1983年12月3日，在新宪法颁布一周年的时候，彭真委员长就进一步贯彻执行宪法的若干问题，向新华社记者发表谈话。', null, '8', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('282', '72', '6', '1', null, null, null, null, 'Img/Image_Related/06-72-1009.jpg', 'Img/Image_Related/06-72-1009.jpg', null, null, '为纪念中华人民共和国六届全国人大一次会议的召开，邮电部于1983年6月6日发行纪念邮票一套。第一枚：中华人民共和国第六届全国人民代表大会第一次会议隆重召开。第二枚：中华人民共和国国歌《义勇军进行曲》。', null, '8', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('283', '72', '6', '1', null, null, null, null, 'Img/Image_Related/06-72-1010.jpg', 'Img/Image_Related/06-72-1010.jpg', null, null, '1984年10月27日至11月1日，由全国人大教科文卫委员会召开教育立法座谈会，周谷城副委员长以及40多位教育专家、学者和有关方面的负责人参加了会议。', null, '12', '0', null, '2014-09-22 12:17:04', null);
