INSERT INTO `image_related` VALUES ('286', '242', '6', '1', null, null, null, null, 'Img/Image_Related/06-72-1013.jpg', 'Img/Image_Related/06-72-1013.jpg', null, null, '1983年9月7日至15日，以彭真委员长为团长的中国党政代表团应邀参加朝鲜国庆35周年庆典活动，受到朝鲜各界的热烈欢迎。', null, '1', '0', null, '2014-09-22 12:17:04', null);
