INSERT INTO `image_related` VALUES ('297', '80', '6', '1', null, null, null, null, 'Img/Image_Related/06-74-1002.jpg', 'Img/Image_Related/06-74-1002.jpg', null, null, '在六届全国人大三次会议上，中国银行香港分行高级副总经理方善桂代表（前排右一）和香港华南电影工作者联合会理事长石慧代表（三排右二）与代表们一起举手表决关于批准中英关于香港问题的联合声明的决定。', null, '4', '0', null, '2014-09-22 12:17:04', null);
