INSERT INTO `image_related` VALUES ('466', '125', '9', '1', null, null, null, null, 'Img/Image_Related/09-125-1015.jpg', 'Img/Image_Related/09-125-1015.jpg', null, null, '1998年3月6日，国家计划委员会主任陈锦华在九届全国人大一次会议上作《关于1997年国民经济和社会发展计划执行情况与1998年国民经济和社会发展计划草案的报告》。', null, '17', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('467', '125', '9', '1', null, null, null, null, 'Img/Image_Related/09-125-1016.jpg', 'Img/Image_Related/09-125-1016.jpg', null, null, '1998年3月5日，李鹏总理在九届全国人大第一次会议上作政府工作报告。', null, '18', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('468', '125', '9', '1', null, null, null, null, 'Img/Image_Related/09-125-1017.jpg', 'Img/Image_Related/09-125-1017.jpg', null, null, '1998年3月19日，国家主席江泽民在九届全国人大一次会议上讲话。', null, '19', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('469', '125', '9', '1', null, null, null, null, 'Img/Image_Related/09-125-1018.jpg', 'Img/Image_Related/09-125-1018.jpg', null, null, ' 1998年3月11日，在九届全国人大一次会议举行的记者招待会上，西藏自治区人大常委会主任热地（左一）对记者的提问，有问必答。', null, '20', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('470', '125', '9', '1', null, null, null, null, 'Img/Image_Related/09-125-1019.jpg', 'Img/Image_Related/09-125-1019.jpg', null, null, '1998年3月，出席九届全国人大一次会议的黑龙江省代表尤建宏（左，赫哲族）、方春子（朝鲜族）。', null, '21', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('471', '125', '9', '1', null, null, null, null, 'Img/Image_Related/09-125-1020.jpg', 'Img/Image_Related/09-125-1020.jpg', null, null, '1998年6月16日，九届全国人大常委会在北京人民大会堂举办第一次法制讲座，请中国人民大学教授许崇德讲授《我国宪法与宪法的实施》。李鹏委员长主持讲座并发表讲话。', null, '22', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('472', '125', '9', '1', null, null, null, null, 'Img/Image_Related/09-125-1021.jpg', 'Img/Image_Related/09-125-1021.jpg', null, null, '1998年12月16日，李鹏委员长参观由全国人大常委会办公厅举办的民主法制建设图片展。', null, '23', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('473', '125', '9', '1', null, null, null, null, 'Img/Image_Related/09-125-1023.jpg', 'Img/Image_Related/09-125-1023.jpg', null, null, ' 1998年7月13日，李鹏委员长在吉林大学举行座谈会，听取在长春的部分全国人大代表和吉林13所高校党委书记、校长、教授对高等教育法草案的修改意见。', null, '25', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('474', '125', '9', '1', null, null, null, null, 'Img/Image_Related/09-125-1024.jpg', 'Img/Image_Related/09-125-1024.jpg', null, null, '1998年7月7日，李鹏委员长在四平市梨树县梨树乡霍家店村就村民委员会组织法修订草案进行调研。这是他在村民薄淑兰（左一）家了解当地村民民主选举干部的情况。', null, '26', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('475', '125', '9', '1', null, null, null, null, 'Img/Image_Related/09-125-1025.jpg', 'Img/Image_Related/09-125-1025.jpg', null, null, '1998年9月1日，全国人大农业与农村委员会邀请在京的部分水利专家、两院院士举行座谈会，讨论依法治水问题。李鹏委员长出席会议并发表重要讲话。', null, '27', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('476', '125', '9', '1', null, null, null, null, 'Img/Image_Related/09-125-1026.jpg', 'Img/Image_Related/09-125-1026.jpg', null, null, '李鹏委员长于1998年10月3日至9日在重庆市、湖北省和三峡库区考察工作。这是1998年10月5日，李鹏委员长来到涪陵清溪镇龙云村看望移民时，同村干部和移民户代表亲切交谈。', null, '28', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('477', '125', '9', '1', null, null, null, null, 'Img/Image_Related/09-125-1027.jpg', 'Img/Image_Related/09-125-1027.jpg', null, null, '1999年12月14日，全国人大环境与资源保护委员会、全国人大农业与农村委员会在北京人民大会堂召开会议，听取黄河治理工作情况的汇报。李鹏委员长参加会议并讲话。', null, '29', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('478', '125', '9', '1', null, null, null, null, 'Img/Image_Related/09-125-1028.jpg', 'Img/Image_Related/09-125-1028.jpg', null, null, '2002年6月27日，九届全国人大常委会第二十八次会议分组审议农业法修订草案、农村土地承包法草案。李鹏委员长参加分组审议。', null, '30', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('479', '125', '9', '1', null, null, null, null, 'Img/Image_Related/09-125-1029.jpg', 'Img/Image_Related/09-125-1029.jpg', null, null, '2002年3月29日，全国人大常委会办公厅、全国人大常委会法制工作委员会在北京人民大会堂举行座谈会，纪念《中华人民共和国全国人民代表大会和地方各级人民代表大会代表法》颁布实施10周年。这是李鹏委员长就深入贯彻实施代表法作重要讲话。', null, '31', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('480', '125', '9', '1', null, null, null, null, 'Img/Image_Related/09-125-1030.jpg', 'Img/Image_Related/09-125-1030.jpg', null, null, '2002年11月6日上午9时50分，李鹏委员长在长江三峡工程导流明渠截流合龙后发表讲话，并来到上游合龙现场，会见三峡工程参建单位代表。', null, '32', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('481', '125', '9', '1', null, null, null, null, 'Img/Image_Related/09-125-1031.jpg', 'Img/Image_Related/09-125-1031.jpg', null, null, '全国人大常委会于2000年9月25日至 28日在北京举办了省级人大常委会主任研讨班。28日，李鹏委员长在研讨班上发表重要讲话。', null, '33', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('482', '125', '9', '1', null, null, null, null, 'Img/Image_Related/09-125-1032.jpg', 'Img/Image_Related/09-125-1032.jpg', null, null, '2000年11月18日，李鹏委员长在重庆邀请当地有关方面专家、学者、人大代表以及相关部门领导召开座谈会，征求大家对婚姻法修正案（草案）和民族区域自治法修正案（草案）的修改意见。 ', null, '34', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('483', '125', '9', '1', null, null, null, null, 'Img/Image_Related/09-125-1035.jpg', 'Img/Image_Related/09-125-1035.jpg', null, null, '1998年9月，李鹏委员长在内蒙古包头钢铁公司考察。', null, '37', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('484', '125', '9', '1', null, null, null, null, 'Img/Image_Related/09-125-1036.jpg', 'Img/Image_Related/09-125-1036.jpg', null, null, '2001年6月9日，全国人大机关举行大型演唱会，庆祝中国共产党成立80周年。右起：程思远、姜春云、李鹏、邹家华、钱木尔・达瓦买提等与大家同台演唱。', null, '38', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('485', '125', '9', '1', null, null, null, null, 'Img/Image_Related/09-125-1037.jpg', 'Img/Image_Related/09-125-1037.jpg', null, null, '2002年，全国人大环资委举办西部开发生态建设座谈会。李鹏委员长参加了座谈会。', null, '39', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('486', '125', '9', '1', null, null, null, null, 'Img/Image_Related/09-125-1038.jpg', 'Img/Image_Related/09-125-1038.jpg', null, null, '2002年，全国人大常委会农业与农村委员会通过召开座谈会调查了解有关农业立法贯彻实施情况。李鹏委员长参加了座谈会。', null, '40', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('487', '125', '9', '1', null, null, null, null, 'Img/Image_Related/09-125-1039.jpg', 'Img/Image_Related/09-125-1039.jpg', null, null, '全国人大内务司法委员会召开研讨会，了解贯彻实施村民委员会组织法的情况。', null, '41', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('488', '137', '9', '2', null, null, null, null, 'Img/Image_Related/09-137-2001.flv', 'Img/Image_Related/09-137-2001.jpg', null, null, '通过村民委员会组织法', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('489', '137', '9', '1', null, null, null, null, 'Img/Image_Related/09-137-1001.jpg', 'Img/Image_Related/09-137-1001.jpg', null, null, '1998年11月4日，九届全国人大常委会第五次会议通过《中华人民共和国村民委员会组织法》', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('490', '129', '9', '2', null, null, null, null, 'Img/Image_Related/09-129-2001.flv', 'Img/Image_Related/09-129-2001.jpg', null, null, '通过证券法', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('491', '126', '9', '2', null, null, null, null, 'Img/Image_Related/09-126-2001.flv', 'Img/Image_Related/09-126-2001.jpg', null, null, '通过宪法修正案', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('492', '126', '9', '1', null, null, null, null, 'Img/Image_Related/09-126-1001.jpg', 'Img/Image_Related/09-126-1001.jpg', null, null, '1999年3月5日，参加九届全国人大二次会议的代表们步出会场。', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('493', '126', '9', '1', null, null, null, null, 'Img/Image_Related/09-126-1002.jpg', 'Img/Image_Related/09-126-1002.jpg', null, null, '1999年3月8日，国务院总理朱熔基参加了九届全国人大二次会议青海代表团审议并听取代表们对政府工作报告的意见。', null, '3', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('494', '126', '9', '1', null, null, null, null, 'Img/Image_Related/09-126-1003.jpg', 'Img/Image_Related/09-126-1003.jpg', null, null, '1999年3月15日，九届全国人大二次会议在宪法修正案草案通过后，代表们热烈鼓掌。', null, '4', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('495', '126', '9', '1', null, null, null, null, 'Img/Image_Related/09-126-1004.jpg', 'Img/Image_Related/09-126-1004.jpg', null, null, '1999年3月5日至15日，九届全国人大二次会议在北京举行。', null, '5', '0', null, '2014-09-22 12:17:04', null);
