INSERT INTO `image_related` VALUES ('497', '128', '9', '2', null, null, null, null, 'Img/Image_Related/09-128-2002.flv', 'Img/Image_Related/09-128-2002.jpg', null, null, '通过合同法', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('498', '131', '9', '2', null, null, null, null, 'Img/Image_Related/09-131-2001.flv', 'Img/Image_Related/09-131-2001.jpg', null, null, '通过关于设立预算工作委员会的决定', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('499', '131', '9', '1', null, null, null, null, 'Img/Image_Related/09-131-1001.jpg', 'Img/Image_Related/09-131-1001.jpg', null, null, '全国人大财经委员会和全国人大常委会预算工作委员会主办立法机关与预算国际研讨会。', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('500', '127', '9', '2', null, null, null, null, 'Img/Image_Related/09-127-2001.flv', 'Img/Image_Related/09-127-2001.jpg', null, null, '通过立法法（一）', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('501', '127', '9', '2', null, null, null, null, 'Img/Image_Related/09-127-2002.flv', 'Img/Image_Related/09-127-2002.jpg', null, null, '通过立法法（二）', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('502', '127', '9', '1', null, null, null, null, 'Img/Image_Related/09-127-1001.jpg', 'Img/Image_Related/09-127-1001.jpg', null, null, '2000年3月5日，第九届全国人民代表大会第三次会议在北京人民大会堂开幕。这是党和国家领导人江泽民、李鹏、朱熔基、李瑞环、胡锦涛、尉健行、李岚清走上主席台。 ', null, '3', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('503', '127', '9', '1', null, null, null, null, 'Img/Image_Related/09-127-1002.jpg', 'Img/Image_Related/09-127-1002.jpg', null, null, '2000年3月7日，九届全国人大三次会议举行记者招待会，国家经贸委主任盛华仁、劳动和社会保障部部长张左已就国有企业改革和社会保障工作回答记者提问。', null, '4', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('504', '132', '9', '2', null, null, null, null, 'Img/Image_Related/09-132-2001.flv', 'Img/Image_Related/09-132-2001.jpg', null, null, '李鹏出席千年议长大会', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('505', '132', '9', '2', null, null, null, null, 'Img/Image_Related/09-132-2002.flv', 'Img/Image_Related/09-132-2002.jpg', null, null, '李鹏出席千年议长大会并发言', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('506', '132', '9', '1', null, null, null, null, 'Img/Image_Related/09-132-1001.jpg', 'Img/Image_Related/09-132-1001.jpg', null, null, '李鹏委员长于1999年11月15日至12月4日对毛里求斯、南非、肯尼亚、以色列、巴勒斯坦和阿曼等亚非6国进行了正式友好访问。图为1999年11月27日，李鹏委员长在巴勒斯坦拉马拉总统府与巴勒斯坦总统阿拉法特会谈后会见记者，并回答记者提问。', null, '3', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('507', '132', '9', '1', null, null, null, null, 'Img/Image_Related/09-132-1002.jpg', 'Img/Image_Related/09-132-1002.jpg', null, null, '2000年9月15日，李鹏委员长乘专机抵达新西伯利亚，对俄罗斯进行访问。这是在机场，身穿俄罗斯民族服装的女青年向李鹏委员长献上面包和盐。', null, '4', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('508', '132', '9', '1', null, null, null, null, 'Img/Image_Related/09-132-1003.jpg', 'Img/Image_Related/09-132-1003.jpg', null, null, '1998年3月23日，正在北京访问的美国哈佛大学校长尼尔・陆登庭博士（左一）率该代表团到欧美同学会做客。这是全国人大常委会副委员长吴阶平（右二）、丁石孙（右一）向陆登庭博士及夫人送礼品。', null, '5', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('509', '132', '9', '1', null, null, null, null, 'Img/Image_Related/09-132-1004.jpg', 'Img/Image_Related/09-132-1004.jpg', null, null, '1998年6月13日，菲律宾总统拉莫斯在总统府会见前来参加菲律宾独立百年庆典的全国人大常委会副委员长谢非。', null, '6', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('510', '132', '9', '1', null, null, null, null, 'Img/Image_Related/09-132-1005.jpg', 'Img/Image_Related/09-132-1005.jpg', null, null, '1999年11月18日，肯尼亚总统莫伊 （右）在内罗华会见到访的全国人大常委会副委员长、全国妇联主席彭佩云（中）。', null, '7', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('511', '132', '9', '1', null, null, null, null, 'Img/Image_Related/09-132-1006.jpg', 'Img/Image_Related/09-132-1006.jpg', null, null, '2001年1月16日，亚太议会论坛第九届年会在智利瓦尔帕莱索市开幕，来自中国、澳大利亚等25个国家的近300名代表参加了此次为期三天的会议。这是中国代表团团长、全国人大常委会副委员长成思危在论坛大会上发言。', null, '8', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('512', '132', '9', '1', null, null, null, null, 'Img/Image_Related/09-132-1007.jpg', 'Img/Image_Related/09-132-1007.jpg', null, null, '2001年11月12日，何鲁丽副委员长在北京人民大会堂会见联合国艾滋病规划署执行主任皮奥特博士一行。这是皮奥特博士向何鲁丽副委员长赠送有关如何防治艾滋病的书籍。', null, '9', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('513', '132', '9', '1', null, null, null, null, 'Img/Image_Related/09-132-1008.jpg', 'Img/Image_Related/09-132-1008.jpg', null, null, '2002年6月25日，2002中俄科技论坛在莫斯科开幕。图为率团参加论坛的全国人大常委会副委员长周光召在论坛上讲演。', null, '10', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('514', '132', '9', '1', null, null, null, null, 'Img/Image_Related/09-132-1009.jpg', 'Img/Image_Related/09-132-1009.jpg', null, null, '2001年5月28日，科特迪瓦议长马马杜・库利巴利在阿比让会见许嘉璐副委员长率领的中国人大代表团一行。', null, '11', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('515', '130', '9', '2', null, null, null, null, 'Img/Image_Related/09-130-2001.flv', 'Img/Image_Related/09-130-2001.jpg', null, null, '为加入世界贸易组织奠定法律基础', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('516', '134', '9', '2', null, null, null, null, 'Img/Image_Related/09-134-2001.flv', 'Img/Image_Related/09-134-2001.jpg', null, null, '党的十六大开幕，李鹏主持', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('517', '134', '9', '2', null, null, null, null, 'Img/Image_Related/09-134-2002.flv', 'Img/Image_Related/09-134-2002.jpg', null, null, '党的十六大开幕', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('518', '135', '9', '2', null, null, null, null, 'Img/Image_Related/09-135-2001.flv', 'Img/Image_Related/09-135-2001.jpg', null, null, '首都各界纪念宪法公布施行20周年大会', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('519', '135', '9', '1', null, null, null, null, 'Img/Image_Related/09-135-1001.jpg', 'Img/Image_Related/09-135-1001.jpg', null, null, '2001年12月3日，中共中央宣传部、全国人大常委会法制工作委员会、司法部在北京人民大会堂举行全国法制宣传日座谈会。李鹏委员长出席座谈会并发表讲话。', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('520', '135', '9', '1', null, null, null, null, 'Img/Image_Related/09-135-1002.jpg', 'Img/Image_Related/09-135-1002.jpg', null, null, '2001年12月4日是全国法制宣传日，全国各地以各种形式开展法律咨询和普法宣传活动。图为山西省临汾市司法局干部在向市民发放法律宣传材料。', null, '3', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('521', '135', '9', '1', null, null, null, null, 'Img/Image_Related/09-135-1003.jpg', 'Img/Image_Related/09-135-1003.jpg', null, null, '2002年12月4日是全国法制宣传日。这是江西鹰潭市百佳城社区居民在参加市司法局等单位组织的法律知识竞赛。', null, '4', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('522', '135', '9', '1', null, null, null, null, 'Img/Image_Related/09-135-1004.jpg', 'Img/Image_Related/09-135-1004.jpg', null, null, '2002年2月，驻守在革命老区遵义市仡佬族苗族自治县的武警部队官兵组成多支义务普法小组，利用春节农闲走村串户，送法下乡，进行法制宣传，受到群众的欢迎。', null, '5', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('523', '135', '9', '1', null, null, null, null, 'Img/Image_Related/09-135-1005.jpg', 'Img/Image_Related/09-135-1005.jpg', null, null, '西藏日喀则市人大常委会组织全市司法机关干部进行普法考试。', null, '6', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('524', '135', '9', '1', null, null, null, null, 'Img/Image_Related/09-135-1006.jpg', 'Img/Image_Related/09-135-1006.jpg', null, null, '2002年12月4日，首都各界举行纪念宪法颁布施行20周年大会，中共中央总书记胡锦涛发表重要讲话。', null, '7', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('525', '136', '9', '1', null, null, null, null, 'Img/Image_Related/09-136-1001.jpg', 'Img/Image_Related/09-136-1001.jpg', null, null, '1999年10月1日，香港特别行政区行政长官董建华、澳门特别行政区行政长官何厚铧在天安门城楼上参加庆祝中华人民共和国成立50周年庆典活动。', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('526', '139', '9', '1', null, null, null, null, 'Img/Image_Related/09-139-1001.jpg', 'Img/Image_Related/09-139-1001.jpg', null, null, '2002年1月21日，福建省和福州市计划生育部门工作人员以及福州市民近千人走上街头，宣传《中华人民共和国人口与计划生育法》颁布。这项法律是2001年12月29日九届全国人大常委会第二十五次会议通过的。', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('527', '140', '9', '1', null, null, null, null, 'Img/Image_Related/09-140-1001.jpg', 'Img/Image_Related/09-140-1001.jpg', null, null, '1998年5月5日，全国人民代表大会澳门特别行政区筹备委员会在北京人民大会堂宣告成立并举行第一次全体会议。李鹏委员长在会上讲话。', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('528', '140', '9', '1', null, null, null, null, 'Img/Image_Related/09-140-1002.jpg', 'Img/Image_Related/09-140-1002.jpg', null, null, '1998年5月5日，全国人民代表大会澳门特别行政区筹委会在北京成立。', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('529', '140', '9', '1', null, null, null, null, 'Img/Image_Related/09-140-1003.jpg', 'Img/Image_Related/09-140-1003.jpg', null, null, '1998年5月5日，“澳门宣传周”的首次活动“今日澳门”摄影展暨邮票展在北京中国历史博物馆开幕。这是王光英副委员长在参观展览。', null, '3', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('530', '140', '9', '1', null, null, null, null, 'Img/Image_Related/09-140-1004.jpg', 'Img/Image_Related/09-140-1004.jpg', null, null, '1999年12月17日，在九届全国人大常委会第十三次会议上，全国人大澳门特别行政区筹委会主任委员钱其琛报告筹委会的工作情况。 ', null, '4', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('531', '140', '9', '1', null, null, null, null, 'Img/Image_Related/09-140-1005.jpg', 'Img/Image_Related/09-140-1005.jpg', null, null, '1999年12月20日，澳门庆祝回归祖国活动委员会举办《迈向美好明天大巡游》，庆祝澳门回归祖国。图为澳门劳工子弟学校的巡游队。', null, '5', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('532', '140', '9', '1', null, null, null, null, 'Img/Image_Related/09-140-1006.jpg', 'Img/Image_Related/09-140-1006.jpg', null, null, '1999年12月19日午夜至20日凌晨， “北京市人民迎接澳门回归祖国联欢晚会”在天安门广场澳门回归倒计时牌前举行。北京市领导及首都各界群众约3万人汇聚广场，欢庆中华民族的这一历史盛事。图为倒计时牌显示0秒时，广场上人群欢动。左右两侧大字为“澳门，你好。”', null, '6', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('533', '140', '9', '1', null, null, null, null, 'Img/Image_Related/09-140-1007.jpg', 'Img/Image_Related/09-140-1007.jpg', null, null, '1999年12月19日午夜，中华人民共和国与葡萄牙共和国在澳门文化中心花园馆隆重举行澳门政权交接仪式。图为1999年12月20日零时，中华人民共和国从此恢复对澳门行使主权。', null, '7', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('534', '140', '9', '1', null, null, null, null, 'Img/Image_Related/09-140-1008.jpg', 'Img/Image_Related/09-140-1008.jpg', null, null, '2001年10月11日，澳门特别行政区全国人大代表视察团一行8人在安徽省合肥市视察。图为视察团在合肥国家高新技术开发区考察投资环境。', null, '8', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('535', '140', '9', '1', null, null, null, null, 'Img/Image_Related/09-140-1009.jpg', 'Img/Image_Related/09-140-1009.jpg', null, null, '2002年12月7日，澳门特别行政区第十届全国人民代表大会代表选举会议在旅游塔会展中心举行第三次会议，正式选举产生12名澳门特别行政区第十届全国人民代表大会代表。这是第一次由澳门居民组成的选举会议选出全部澳门特别行政区全国人大代表。会后，选举会议主席团发表公告公布了选举结果，并报全国人大常委会代表资格审查委员会进行资格审查。', null, '9', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('536', '231', '9', '1', null, null, null, null, 'Img/Image_Related/09-231-1001.jpg', 'Img/Image_Related/09-231-1001.jpg', null, null, '2001年3月5日至15日，九届全国人大四次会议在北京举行。2001年3月5日，李鹏委员长主持开幕式。', null, '1', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('537', '231', '9', '1', null, null, null, null, 'Img/Image_Related/09-231-1002.jpg', 'Img/Image_Related/09-231-1002.jpg', null, null, '2001年3月5日，九届全国人大四次会议开幕。党和国家领导人江泽民、李鹏、朱熔基、李瑞环、胡锦涛、尉健行、李岚清步入会场。', null, '2', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('538', '231', '9', '1', null, null, null, null, 'Img/Image_Related/09-231-1003.jpg', 'Img/Image_Related/09-231-1003.jpg', null, null, '出席九届全国人大四次会议的全国人大代表、内蒙古自治区主席乌云其木格在小组会上指出，内蒙古作为西部大开发的一员，要充分利用中央给的政策，加快发展。', null, '3', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('539', '231', '9', '1', null, null, null, null, 'Img/Image_Related/09-231-1004.jpg', 'Img/Image_Related/09-231-1004.jpg', null, null, '2001年3月，出席九届全国人大四次会议的湖北代表在审议《中华人民共和国中外合资经营企业办法修正案（草案）》时认为，这个修正案十分必要，在加入　WTO后，便于我国的产品同国际惯例接轨。', null, '4', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('540', '231', '9', '1', null, null, null, null, 'Img/Image_Related/09-231-1005.jpg', 'Img/Image_Related/09-231-1005.jpg', null, null, '2001年3月，在九届全国人大四次会议期间，全国人大代表、山西省妇联主席梁豫秦提出三项关于妇女儿童权益保护的议案，得到代表们的支持。', null, '5', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('541', '231', '9', '1', null, null, null, null, 'Img/Image_Related/09-231-1006.jpg', 'Img/Image_Related/09-231-1006.jpg', null, null, '2001年3月1日，刚刚抵达北京的广西壮族自治区人大代表团团长、自治区党委书记曹伯纯前去看望94岁的全国人大常委会副委员长程思远。', null, '6', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('542', '231', '9', '1', null, null, null, null, 'Img/Image_Related/09-231-1007.jpg', 'Img/Image_Related/09-231-1007.jpg', null, null, '2001年，九届全国人大四次会议收到代表议案数突破千件。', null, '7', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('543', '231', '9', '1', null, null, null, null, 'Img/Image_Related/09-231-1008.jpg', 'Img/Image_Related/09-231-1008.jpg', null, null, '2001年3月9日，全国人大常委会委员、全国人大环境与资源保护委员会主任委员曲格平，全国人大常委会委员、全国人大环境与资源保护委员会委员王涛、孙鸿烈等就如何从立法方面加强环境保护等问题接受记者集体采访。', null, '8', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('544', '231', '9', '1', null, null, null, null, 'Img/Image_Related/09-231-1009.jpg', 'Img/Image_Related/09-231-1009.jpg', null, null, '2001年3月10日，在全国人大代表听取了最高人民法院和最高人民检察院的工作报告后，山西代表团段丽卿代表建议，法院和检察院应加强信访工作，建立院长轮流值班制度。', null, '9', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('545', '231', '9', '1', null, null, null, null, 'Img/Image_Related/09-231-1010.jpg', 'Img/Image_Related/09-231-1010.jpg', null, null, '2001年3月7日，江苏省妇联和南京市妇联联合举办大型广场咨询和演出活动。法律、教育、卫生等方面的专家和社会学者向群众宣传《婚姻法（修正草案）》等法律。  ', null, '10', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('546', '231', '9', '1', null, null, null, null, 'Img/Image_Related/09-231-1011.jpg', 'Img/Image_Related/09-231-1011.jpg', null, null, '2001年3月5日，姜春云在九届全国人大四次会议吉林代表团小组会上发言。', null, '11', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('547', '231', '9', '1', null, null, null, null, 'Img/Image_Related/09-231-1012.jpg', 'Img/Image_Related/09-231-1012.jpg', null, null, '1998年8月26日，九届全国人大常委会第四次会议在北京人民大会堂举行全体会议，听取国务院副总理温家宝关于当前全国抗洪抢险情况的报告。这是大会会场。', null, '12', '0', null, '2014-09-22 12:17:04', null);
