INSERT INTO `image_related` VALUES ('565', '142', '10', '1', null, null, null, null, 'Img/Image_Related/10-142-1004.jpg', 'Img/Image_Related/10-142-1004.jpg', null, null, '2003年3月10日，李鹏委员长在十届全国人大一次会议上，作全国人大常委会工作报告。', null, '22', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('566', '142', '10', '1', null, null, null, null, 'Img/Image_Related/10-142-1005.jpg', 'Img/Image_Related/10-142-1005.jpg', null, null, '这是出席十届全国人大一次会议的代表步入会场。', null, '32', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('567', '142', '10', '1', null, null, null, null, 'Img/Image_Related/10-142-1006.jpg', 'Img/Image_Related/10-142-1006.jpg', null, null, '这是出席十届全国人大一次会议的少数民族代表。在十届全国人大一次会议之前选出的2984名代表中，全国55个少数民族都有本民族的代表，共415人，占代表总人数的13.91%。', null, '33', '0', null, '2014-09-22 12:17:04', null);
