INSERT INTO `image_related` VALUES ('571', '142', '10', '1', null, null, null, null, 'Img/Image_Related/10-142-1010.jpg', 'Img/Image_Related/10-142-1010.jpg', null, null, '2003年3月15日，在十届全国人大一次会议第五次全体会议上，选举第十届全国人民代表大会常委委员会委员长、副委员长、秘书长；选举第十届全国人民代表大会常务委员会委员；选举中华人民共和国主席、副主席等国家领导人。', null, '27', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('572', '142', '10', '1', null, null, null, null, 'Img/Image_Related/10-142-1011.jpg', 'Img/Image_Related/10-142-1011.jpg', null, null, '2003年3月10日，在十届全国人大一次会议上，代表们认真听取李鹏委员长作全国人大常委会工作报告。', null, '25', '0', null, '2014-09-22 12:17:04', null);
