INSERT INTO `image_related` VALUES ('575', '142', '10', '1', null, null, null, null, 'Img/Image_Related/10-142-1014.jpg', 'Img/Image_Related/10-142-1014.jpg', null, null, '2003年3月5日，出席十届全国人大一次会议的云南代表团在驻地审议政府工作报告。', null, '18', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('576', '142', '10', '1', null, null, null, null, 'Img/Image_Related/10-142-1015.jpg', 'Img/Image_Related/10-142-1015.jpg', null, null, '出席十届全国人大一次会议的广西代表、贵港市江南中学副校长周桂英（壮族）说，发展少数民族教育要根据少数民族地区的经济和教育特点，给予政策、法律、资金、项目、人才等方面的扶持。', null, '34', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('577', '142', '10', '1', null, null, null, null, 'Img/Image_Related/10-142-1016.jpg', 'Img/Image_Related/10-142-1016.jpg', null, null, '2003年3月6日，出席十届全国人大一次会议的湖北代表、长江水利委员会主任蔡其华说，长江防洪体系的全面加强，使长江中下游地区战胜了这几年的洪水，保障了长江流域防洪安全。', null, '19', '0', null, '2014-09-22 12:17:04', null);
