INSERT INTO `image_related` VALUES ('580', '142', '10', '1', null, null, null, null, 'Img/Image_Related/10-142-1019.jpg', 'Img/Image_Related/10-142-1019.jpg', null, null, '2003年3月18日，吴邦国委员长在十届全国人大一次会议闭幕会上讲话。', null, '30', '0', null, '2014-09-22 12:17:04', null);
