INSERT INTO `image_related` VALUES ('582', '142', '10', '1', null, null, null, null, 'Img/Image_Related/10-142-1021.jpg', 'Img/Image_Related/10-142-1021.jpg', null, null, '2003年3月17日，十届全国人大一次会议在北京人民大会堂举行第七次全体会议。表决全国人大各专门委员会组成人员；决定国务院副总理、国务委员、各部部长、各委员会主任、中国人民银行行长、审计长、秘书长人选。这是大会执行主席司马义・艾买提主持大会。', null, '29', '0', null, '2014-09-22 12:17:04', null);
