INSERT INTO `image_related` VALUES ('585', '141', '10', '1', null, null, null, null, 'Img/Image_Related/10-142-1024.jpg', 'Img/Image_Related/10-142-1024.jpg', null, null, '2003年6月28日，吴邦国委员长主持十届全国人大常委会第二次法制讲座。这次法制讲座分别由全国人大常委会副秘书长刘镇讲授《全国人大常委会的监督工作》，全国人大法律委员会委员张春生讲授《关于全国人大常委会的组织制度和议事制度》。', null, '1', '0', null, '2014-09-22 12:17:04', null);
