INSERT INTO `image_related` VALUES ('614', '143', '10', '1', null, null, null, null, 'Img/Image_Related/10-143-1022.jpg', 'Img/Image_Related/10-143-1022.jpg', null, null, '2004年3月12日，出席十届全国人大二次会议的中国载人航天工程副总指挥胡世祥代表在接受新华社记者专访时说，中国计划于2005年晚些时候开始选拔女航天员，选拔将在全国各地、各界的女性中展开，包括港、澳地区。', null, '24', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('615', '143', '10', '1', null, null, null, null, 'Img/Image_Related/10-143-1023.jpg', 'Img/Image_Related/10-143-1023.jpg', null, null, '出席十届全国人大二次会议的黑龙江代表团代表杨信在发言中说，黑龙江是我国产粮大省，要加大对绿色产品的支持力度，建立绿色产品生产基地，提升农业产品档次。', null, '31', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('616', '143', '10', '1', null, null, null, null, 'Img/Image_Related/10-143-1024.jpg', 'Img/Image_Related/10-143-1024.jpg', null, null, '出席十届全国人大二次会议上的申纪兰代表在认真听取报告。山西省长治市人大常委会副主任申纪兰是唯一的一至十届全国人大代表。1954年，她是骑着毛驴走出平顺县西沟村到北京参加第一届全国人民代表大会的。', null, '32', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('617', '143', '10', '1', null, null, null, null, 'Img/Image_Related/10-143-1025.jpg', 'Img/Image_Related/10-143-1025.jpg', null, null, '2004年3月5日下午，胡锦涛参加十届全国人大二次会议西藏代表团审议。这是胡锦涛同志与代表们亲切交谈。', null, '8', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('618', '143', '10', '1', null, null, null, null, 'Img/Image_Related/10-143-1026.jpg', 'Img/Image_Related/10-143-1026.jpg', null, null, '2004年3月6日，江泽民参加了他所在的上海代表团会议，同代表们一起审议政府工作报告。中共中央政治局常委、国务院副总理黄菊也参加了审议。', null, '9', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('619', '143', '10', '1', null, null, null, null, 'Img/Image_Related/10-143-1027.jpg', 'Img/Image_Related/10-143-1027.jpg', null, null, '2004年3月14日下午，温家宝总理在北京人民大会堂会见中外记者，并回答记者提问。', null, '26', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('620', '143', '10', '1', null, null, null, null, 'Img/Image_Related/10-143-1028.jpg', 'Img/Image_Related/10-143-1028.jpg', null, null, '2004年3月14日下午，吴邦国委员长主持十届全国人大二次会议闭幕会。', null, '27', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('621', '143', '10', '1', null, null, null, null, 'Img/Image_Related/10-143-1029.jpg', 'Img/Image_Related/10-143-1029.jpg', null, null, '2004年3月14日下午，代表投票表决《中华人民共和国宪法修正案（草案）》。', null, '28', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('622', '143', '10', '1', null, null, null, null, 'Img/Image_Related/10-143-1030.jpg', 'Img/Image_Related/10-143-1030.jpg', null, null, '2004年3月14日下午，十届全国人大二次会议在北京人民大会堂举行闭幕会。代表投票表决《中华人民共和国宪法修正案（草案）》。这是总监票人公保尚代表（藏族）在投票。', null, '29', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('623', '143', '10', '1', null, null, null, null, 'Img/Image_Related/10-143-1031.jpg', 'Img/Image_Related/10-143-1031.jpg', null, null, '图表：第十届全国人民代表大会一年工作回顾。', null, '33', '0', null, '2014-09-22 12:17:04', null);
INSERT INTO `image_related` VALUES ('624', '143', '10', '1', null, null, null, null, 'Img/Image_Related/10-143-1032.jpg', 'Img/Image_Related/10-143-1032.jpg', null, null, '图表:中国现行宪法第四次修改经过回放。', null, '34', '0', null, '2014-09-22 12:17:04', null);
