INSERT INTO `paragraph` VALUES ('1308', '113', '', '１９８１―１９８３年　上海市仪表电讯工业局党委副书记', '8', '2014-09-20 16:28:31');
INSERT INTO `paragraph` VALUES ('1309', '113', '', '１９８３―１９８５年　上海市委常委兼市委科技工作党委书记', '9', '2014-09-20 16:28:31');
INSERT INTO `paragraph` VALUES ('1310', '113', '', '１９８５―１９９１年　上海市委副书记', '10', '2014-09-20 16:28:31');
